SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;
SET time_zone = "+00:00";

/*!40101 SET @OLD_CHARACTER_SET_CLIENT=@@CHARACTER_SET_CLIENT */;
/*!40101 SET @OLD_CHARACTER_SET_RESULTS=@@CHARACTER_SET_RESULTS */;
/*!40101 SET @OLD_COLLATION_CONNECTION=@@COLLATION_CONNECTION */;
/*!40101 SET NAMES utf8mb4 */;


DELIMITER $$
CREATE DEFINER=`rapida`@`localhost` PROCEDURE `exec_q` (IN `q` VARCHAR(10000) CHARSET utf8)  NO SQL
BEGIN
  SET @tquery = q;
  PREPARE stmt FROM @tquery;
  EXECUTE stmt;
  DEALLOCATE PREPARE stmt;
END$$

CREATE DEFINER=`rapida`@`localhost` PROCEDURE `looptable` ()  BEGIN
  DECLARE done BOOLEAN DEFAULT FALSE;
  DECLARE str VARCHAR(2048);
  DECLARE cur CURSOR FOR SELECT q from t_temp WHERE q is not null;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done := TRUE;

  OPEN cur;

  foreach: LOOP
    FETCH cur INTO str;
    IF done THEN
      LEAVE foreach;
    END IF;
    CALL exec_q(str);
  END LOOP foreach;
  CLOSE cur;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `TRANSLIT` (`str` TEXT) RETURNS TEXT CHARSET utf8 SQL SECURITY INVOKER
BEGIN
declare str2 varchar(5);
declare str3 text;
declare len int(11);
declare i int(11);
set str3 = '';
set i = 1;
set len = char_length(str);
/*идем циклом по символам строки*/
while i <= len do  /*выполняем преобразование припомощи фии ELT */
 set str2 = elt(
   instr(
     'АаАБбВвГгДдЕеЁёЖжЗзИиЙйКкЛлМмНнОоПпРрСсТтУуфХхцЦЧчШшЩщЪъЫыЬьЭэЮюЯя /%',
     substr(str,i,1)
   ),
'A','a','A','B','b','V','v','G','g','D','d','E','e','YO','yo','ZH','zh','Z','z','I','i','J','j','K','k','L','l','M','m',
'N','n','O','o','P','p','R','r','S','s','T','t','U','u','f','KH','kh','c','C','CH','ch','SH','sh','SHH','shh','\'\'','\'\'',
'YI','yi','\'','\'','EH','eh','YU','yu','YA','ya','_','__','_pct_'
 );
 
 
 if str2 is null then /* если преобразование не прошло успешно добавляем в резт исходный символ */
    set str2 = substr(str,i,1);
 end if;
 set str3 = concat(str3,str2);
 set i = i + 1;
end while;

return lower(str3);

END$$

DELIMITER ;

CREATE TABLE `s_blog` (
  `id` mediumint(8) UNSIGNED NOT NULL,
  `name` varchar(500) NOT NULL,
  `trans` varchar(255) DEFAULT '',
  `image` varchar(255) NOT NULL DEFAULT '',
  `image_id` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `meta_title` varchar(500) DEFAULT '',
  `meta_keywords` varchar(500) DEFAULT '',
  `meta_description` varchar(500) DEFAULT '',
  `annotation` text,
  `text` longtext,
  `visible` tinyint(1) UNSIGNED NOT NULL DEFAULT '0',
  `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

INSERT INTO `s_blog` (`id`, `name`, `trans`, `image`, `image_id`, `meta_title`, `meta_keywords`, `meta_description`, `annotation`, `text`, `visible`) VALUES
(1, 'Подвесные светильники всему голова!', 'podvesnye-svetilniki-vsemu-golova6', '2ff0198a81469a6f381425ae75f979c8.jpg', 48, 'Подвесные светильники всему голова!', 'Подвесные светильники всему голова!', 'Кратко о подвесных светильниках', '<p>Самыми первыми в мире светильниками подвесного типа под названием паникадило (по-гречески &laquo;многосвечение&raquo;) в прошлом оснащали церковные здания. Изготовленные из металлов, эти архаичные <a href=\"/simpla/&quot;../catalog/svetilniki-podvesnye\">подвесные светильники</a>&nbsp;были подвешены на цепях. Паникадила отличались большими размерами и могли включать в себя множество ярусов с большим числом подвесных компонентов. Отголоски прошлых эпох можно увидеть у современных люстр классического стиля.</p>', '<h1>Подвесные светильники</h1><p>Подвесные светильники способны выполнять множество функций, от утилитарных до эстетических.</p><p>Основная задача обеспечивать необходимый уровень освещения в помещениях. С помощью грамотно подобранной люстры можно не только освещать комнату, но и придать ей абсолютно новый внешний вид.</p><p>Разнообразие ассортимента дает возможность выбрать светильники для любого типа интерьеров. Например, для барокко, рококо или классики, наполненных подходящей меблировкой, лепными элементами и колоннами, отлично подойдут образцы с декором из металлических деталей, рисунками классического направления и оригинальными подвесками из хрусталя.</p><p>Для стилей интерьера хай-тек с минимализмом нужно подбирать и светильники аналогичного формата. Отличительными факторами будут оригинальность внешнего вида, холодные расцветки и строгость линий дизайна.</p><p>Образцы на основе металла с матовым стеклом в лаконичном стиле подойдут для любого типа интерьера.</p><p><img style=\"max-width: 100%;\" src=\"/img/blog/01ff1cb2c43687ed8d12aae38323b3ec.jpg\" alt=\"Светильник подвесной\" title=\"Светильник подвесной в гостиной\" /></p><h2>Подвесные светильники лофт</h2><p>Популярный современный стиль лофт стал воплощением духа творческой свободы. В этом стиле сочетаются разнообразные и как будто и не соединимые элементы прошлых эпох и нынешнего времени. Здесь прекрасно объединяется раритетная классика из прошлого и дорогой эксклюзив современности. Подвесные светильники лофт образец технологического стиля, стирающего границу между простыми и оригинальными вещами. В интерьере, оформленном в стиле лофт, не последнее место занимают соответствующие светильники, которые могут стать настоящей изюминкой в интерьере.</p><p><img style=\"max-width: 100%;\" src=\"/img/blog/8226d0877e5905e192aee4a3f53105db.jpg\" alt=\"Светильник подвесной лофт\" title=\"Светильник подвесной лофт для гостиной\" /></p><h2>Где разместить подвесные светильники?</h2><p>Как правило, самым удобным местом для размещения подвесных световых приборов являются любые помещения с достаточно высокими потолками. Они могут выполнять разные функции. В кабинетах помощью настольного светильника можно выделить рабочую зону, а подвесная люстра обеспечит общее освещение и уменьшит перепады освещённости в вечернее и ночное время.</p><p>Удобно будет размещать одиночные подвесы около прикроватной тумбочки вместо настольной лампы. Это позволит сэкономить место на тумбе, а также украсит дизайн. Тут необходимо только подобрать подходящую длину подвеса, чтобы организовать оптимальное освещение и не занять лишнего места.</p><h2>Подвесные светильники для кухни</h2><p>Отлично смотрятся подвесные светильники для кухни, кухни-гостиной и столовой. Здесь можно акцентировать внимание на люстре, которая будет висеть в центре, освещая всю композицию помещения, а также можно разместить несколько светильников, каждый из которых будут освещать свою часть кухни. При наличии просторной кухни можно поэкспериментировать с несколькими типами светильников, а для небольшой малогабаритной кухоньки будет достаточно одного центрального светильника.</p><p>Подвесной светильник монтируют над кухонными столами, направляя световой поток на столешницу или рабочий стол, что обеспечит хозяйке комфортное освещение при создании кулинарных шедевров.</p><p><img style=\"max-width: 100%;\" src=\"/img/blog/e3c9840ec0bec8bfe0c1ba119586e2c0.jpg\" alt=\"Светильник подвесной Eglo\" title=\"Светильник подвесной Eglo в интерьере\" /></p><h2>Сколько нужно подвесных светильников?</h2><p>Светильником можно удобно зонировать пространство в помещении, а также при необходимости акцентировать внимание на цвете и фактуре материалов отделки. Порой именно светильник играет роль изюминки в интерьере помещения.</p><p>Как понять, какое количество светильников необходимо разместить в той или иной комнате? Однозначного ответа здесь нет. Все слишком сложно и обусловлено типами и размерами помещения, высотой потолка, отдельных задач, цветовых решений и потребностей пользователей. Однако есть определенная градация, обобщающая некоторые факторы.</p><p>Для небольших комнат достаточно одного светильника, а если добавить встроенные точечные приборы, то можно усилить световой поток локально. Для столовых резонно размещать комплекс световых приборов, размещенный у стойки или обеденного стола.</p><p>При выборе объемных моделей достаточно использовать две-три штуки. В комплексные ансамбли будет правильнее включить одну модельную серию, чтобы добавить интерьеру завершенность.</p><p>Дизайнерская фантазия не знает границ, мастера выпускают уникальные искусно сделанные модели с эксклюзивным декорированием. Затейливая роспись на поверхностях, игра светотени могут превратить самую обычную комнату в помещение, где светильники становятся произведением высокого искусства.</p><p>Адекватно выбранные подвесные светильники могут полностью изменить комнату, добавив в интерьер особые комфортные нотки. Сейчас этот тип световых приборов переживает своеобразный ренессанс, находясь на вершине популярности. Это произошло за счет их практичности, высоких потребительских свойств, удобности в использовании и недорогой цене. Огромный выбор светильников разных цветов и форм даст возможность превратить его в центральный объект помещения или сделать его неброским элементом дополнительного освещения.</p><p>Подвесные светильники во всем своем многообразии объединены одним способом крепления и возможностью корректировать высоту лампы и направление светового потока. Этим можно настроить освещение нужным вам образом для любого типа помещения.</p><p><img style=\"max-width: 100%;\" src=\"/img/blog/ab5f1ff05f17622ecc973914aaf73c8f.jpg\" alt=\"Подвесной светильник в интерьере\" title=\"Светильник подвесной в интерьере\" /></p><p>Наш интернет-магазин Sevenlight.ru предлагает вам огромный выбор подвесных светильников любых форм, расцветок и стилевого направления. У нас вы найдете продукцию от ведущих мировых производителей высочайшего качества по доступным ценам. Все подвесные светильники представлены в подробном красочном каталоге с реальными фотографиями и подробными техническими характеристиками. Если вы хотите впустить в свою жизнь больше света, обращайтесь к нам!</p>', 1),
(2, 'Мы сделали фильтр цен', 'my-sdelali-filtr-tsen', '', 0, 'Мы сделали фильтр цен!', 'Мы сделали фильтр цен!', 'Теперь поиск на нашем сайте стал еще удобнее. Теперь вы можете посмотреть не все товары из раздела сразу, а только выбранные бренды и только подходящие под заданные параметры цены.', '<p>Теперь поиск на нашем сайте стал еще удобнее. Теперь вы можете посмотреть не все товары из раздела сразу, а только выбранные бренды и только подходящие под заданные параметры цены.</p>', '<p>Например, &nbsp;вам не нужна китайская люстра или российский светильник. Вам нужны только отдельные итальянские бренды! Нет ничего проще. Просто отмечаете галочкой ваши любимые бренды и нажимаете применить. Сайт скроет все остальные бренды и покажет только те, которые вы выбрали.</p>\r\n<p>Может быть вы не хотите видеть слишком дешевую или слишком дорогую продукцию? Просто выбираете нужный вам диапазон цен, нажимаете кнопку применить и наслаждаетесь выбором.</p>', 0),
(3, 'ОСВЕЩЕНИЕ И ЧЕЛОВЕЧЕСКОЕ ЗДОРОВЬЕ, ПОЧЕМУ СВЕТ ЯВЛЯЕТСЯ НАРКОТИКОМ?', 'osveschenie-i-chelovecheskoe-zdorove-pochemu-svet-yavlyaetsya-narkotikom', '', 0, 'ОСВЕЩЕНИЕ И ЧЕЛОВЕЧЕСКОЕ ЗДОРОВЬЕ, ПОЧЕМУ СВЕТ ЯВЛЯЕТСЯ НАРКОТИКОМ?', 'ОСВЕЩЕНИЕ И ЧЕЛОВЕЧЕСКОЕ ЗДОРОВЬЕ, ПОЧЕМУ СВЕТ ЯВЛЯЕТСЯ НАРКОТИКОМ?', 'В двух словах о том, почему свет оказывает такое влияние на биологические часы наших тел. Растет число исседований, которые доказывают, что освещение удивительно сильно способно воздействовать на человеческое тело. Следует ли нам начать относится к нему, как к наркотику?', '<p><span>В двух словах о том, почему свет оказывает такое влияние на биологические часы наших тел.</span></p><p><span style=\"line-height: 1.42857;\">Растет число исседований, которые доказывают, что освещение удивительно сильно способно воздействовать на человеческое тело. Следует ли нам начать относится к нему, как к наркотику?</span></p><p><span style=\"line-height: 1.42857;\"><br /></span></p>', '<p>Представьте, что вся человеческая эволюция сжалась в один день. Тогда Вы бы научились ходить едва бы выбрались из постели. Затем проведя большую часть утра в охоте на дикого кабана, к полудню вы бы уже занялись сельским хозяйством, научились писать и, наконец, в последние секунды до наступления полночи &ndash; изобрели бы печатный пресс, паровоз, атомную бомбу, лампу накаливания и светодиод.&nbsp;</p><p></p><p><span style=\"line-height: 1.42857;\"><br /> <img src=\"/design/Bootstrap-Blue_v1_1/images/1-1.png\" /> </span></p><p><span style=\"line-height: 1.42857;\"><br /></span></p><p><span style=\"line-height: 1.42857;\">Сейчас в двух словах о том, почему свет оказывает такое влияние на биологические часы наших тел. Человеческая эволюция прошла почти весь свой путь в лучах солнечного света. И хотя искусственное освещение используется уже не одну сотню лет, оно по-прежнему приводит в адское замешательство наш мозг, приученный к солнечному свету еще со времен каменного века.&nbsp;</span></p><p><span style=\"line-height: 1.42857;\">Все мы давно знаем, как включенный свет не дает заснуть, и каково это сделать шаг из самолета ярким солнечным днем, когда ваш мозг говорит вам, что это середина ночи. Но только совсем недавно, стали известны причины этих явлений, и&nbsp;насколько сильное влияние свет оказывает на наши тела и умы.</span></p><p><span style=\"line-height: 1.42857;\">На самом деле ученые уже сейчас предлагают нам рассматривать свет в качестве лекарственного средства, такой огромной силой обладает его воздействия на человеческое тело и его биоритмы.</span></p><p><span style=\"line-height: 1.42857;\">4 ноября 2011 года, шесть космонавтов вернулись после долгого полуторалетнего путешествия на Марс, вышли моргая из своего космического корабля. Но они на самом деле не были на Марсе, их &laquo;космический корабль&raquo; никуда не летал, а стоял все это время в исследовательском институте в Москве, являясь частью уникального эксперимента, целью которого было изучение воздействия&nbsp; на человеческое тело и разум долгого пребывания в замкнутом пространстве.</span></p><p><span style=\"line-height: 1.42857;\">Во время этого псевдопутешествия ученые отслеживали ежедневную активность космонавтов, циклы сна, различные психические показатели, давали им регулярные тесты и опросники, чтобы смотреть, как космонавты справляются с нагрузкой.</span></p><p><span style=\"line-height: 1.42857;\"> <br /> <img src=\"/design/Bootstrap-Blue_v1_1/images/2-2.png\" /> <br /> <br /> Одной из самых больших проблем оказался сон. Результаты исследований, опубликованные в декабре 2012 г., показали, что биологические часы космонавтов быстро расстроились. Не в последнюю очередь потому, что в их космическом корабле не было никакого естественного света. У четырех из шести космонавтов наблюдались значительные изменения в шаблонах фаз сна, вызывая различные проблемы состояния здоровья, как&nbsp;психического, так и физического. Просто плохие новости на стадии эксперимента, стали бы настоящей катастрофой в настоящем космическом полете.</span></p><p><span style=\"line-height: 1.42857;\">За последнее десятилетие ученые сделали без преувеличения важнейшее открытие биодинамического освещения. Все люди, по сути, оснащены датчиками синего света. Специальные рецепторы в наших глазах реагируют на содержание синей составляющей светового спектра. Изменение доли синего цвета в течение дня готовит нас, соответственно, к работе или отдыху. Наш суточный биоцикл напоминает цикл от восхода и до захода солнца. Поэтому искусственное освещение, которое не дает нам правильных сигналов солнца, не в состоянии сохранять наш суточный биоцикл в норме. Результат превосходит легкое недомогание. Плохое освещение может способствовать депрессии (особенно в зимний период), снижению производительности труда, увеличению веса и может увеличить риск развития других проблем со здоровьем или усугубить те, что у нас уже есть. Исследования показали, что пациенты больницы, которые размещаются в более солнечных палатах испытывают меньше стресса, используют меньше обезболивающего и выписываются раньше других.</span></p><p><span style=\"line-height: 1.42857;\">Проблема в российском космическом корабле былав том, что в лампах дневного света не хватало синего элемента, по которому сверяются наши биологические часы. НАСА потратило более 11 млн. долларов на замену люминесцентных ламп дневного освещения на Международной космической станции на светодиоды. Светодиодное освещение, позволяющее контролировать цвет, интенсивность и время освещения помогает космонавтам лучше спать и просыпаться&nbsp;отдохнувшими.</span></p><p>По счастливой случайности наше понимание значимости биологического воздействия света совпало с временем развити массового производства светодиодов и систем управления освещением.</p><p>В интернет-магазине sevenlight.ru<a href=\"/simpla/sevenlight.ru%20\">&nbsp;</a>вы можете подобрать себе продукцию, которую в дальнейшем можно оснастить системой динамического управления света, предлагаем Вам ознакомиться со списком самых ходовых брендов:</p><p><strong><a href=\"/catalog/svetilniki/mw-light\" title=\"Светильники Mw-light\">Светильники Mw-light</a></strong></p><p><strong><a href=\"/catalog/lyustry-podvesnye/arte-lamp\">Подвесные люстры Arte lamp</a></strong></p><p><strong><a href=\"/catalog/lyustry/maytoni\">Люстры Maytoni</a></strong></p><p><br /> <img src=\"/design/Bootstrap-Blue_v1_1/images/3-3.png\" /> <br /> <br /> Настройка цвета и интенсивности света в течение дня позволяет синхронизировать искуственное освещение с человеческими биоритмами.</p><p><span style=\"line-height: 1.42857;\">Компании, в том числе Philips, Osram и Fagerhult способствовали проведению обширных исследований в этой области и разработали готовые решения по&nbsp; динамическому освещению для квартир, домов, школ, офисов и больниц, которые наш интернет-магазин sevenlight.ru Вам с удовольствием представляет.</span></p><p><span style=\"line-height: 1.42857;\">Компания PhotonStar вложила 2 млн. долларов в разработку биодинамического освещения, в том исле беспроводные лампы с управлением цветового потока. PhotonStar считает, что приоритеты производителей осветительных приборов и дизайнеров меняются. &laquo;Сначала речь шла только об удобстве и безопасности, потом об энергоэффективности, потом о долговечности. Следующий уровень &ndash; подумать о здоровье человека и производительности его труда. Установив энергоэффективное освещение вы могли бы сэкономить 40 процентов на электроэнергии, но если вы можете улучшить у человека производительность труда всего лишь на один процент, вы сэкономите гораздо больше. Огромный потенциал!&raquo;. Теперь динамическое освещение доступно не только космонавтам Международной космической станции, sevenlight.ru предлагает готовые решения по динамическому освещения каждому.&nbsp;</span><span style=\"line-height: 1.42857;\">Интернет-магазин sevenlight.ru делает Вас ближе к самым современным технологиям.</span></p><p><span style=\"line-height: 1.42857;\"><br /></span></p><p><span style=\"line-height: 1.42857;\">Вы не из Москвы и Санкт-Петербурга? Никаких проблем, мы отправим Вашу люстру, светильник, бра с динамическим освещением и без в любую точку страны. Владивосток и Благовещенск, Новосибирск и Иркутск - не проблема. Динимический свет начинается с sevenlight.ru</span></p>', 0),
(4, 'Недорогие люстры - большой выбор!', 'nedorogie-lyustry', '', 0, 'Недорогие люстры - большой выбор!', 'Недорогие люстры хорошего качества!', 'Недорогие люстры, большой выбор!', 'Дорогие покупателя! Для вас мы подготовили специалный раздел с недорогими люстрами. Сделали выборку, по цене, типам и материалам!<br />Добро пожаловать в раздел <a href=\"../catalog/nedorogie-lyustry\">НЕДОРОГИЕ ЛЮСТРЫ</a>.', '<h5>В разделе недорогие люстры присутствуют также люстры, участвующие в распродаже.&nbsp;<br /><br />Добро пожаловать в раздел <strong><a href=\"../catalog/nedorogie-lyustry\">НЕДОРОГИЕ ЛЮСТРЫ</a>!&nbsp;</strong></h5>\r\n<br /><br /><br />\r\n<p>&nbsp;</p>', 0),
(5, 'Стильное лофт освещение у себя за дешево?', 'stilnoe-loft-osveschenie-u-sebya-za-deshevo', '8226d0877e5905e192aee4a3f53105db.jpg', 3, 'Стильное лофт освещение у себя за дешево? Предлагаем лофт светильники!', 'Лофт светильники', 'Лофт светильники своими руками!', '<p>Приветствуем Вас дорогие гости!<br />&nbsp;Мы хотели бы предложить на обзор отличную комбинацию двух элементов.<br /><br /><br /><br /></p>', '<p>Первое - это стильный ретро патрон для лофт лампы с цоколем E27. И второе - это сама ретро лампа.<br />Если Вам нужно простое и стильное световое решение декоративного освещение, <br />то с помощью этой пары вы сможете преобразить комнату или какое-либо помещение в лофт плэйс. <br />Ретро лампы создают чарующее атмосферное настроение теплых тонов и это всегда приятно.<br /><br />Стоят же эти элементы копейки.<br /><br />К примеру, ретро патрон Sun lumen - 800 руб. !</p>\r\n<p><a title=\"Ретро патрон Sun lumen\" href=\"../products/retro-patron-s-podvesom-e27-chernyj-sun-lumen-056-717\">Ретро патрон Sun lumen</a><br /><br /><img src=\"../files/uploads/sun-lumen_056-717.1024x768w.jpeg\" alt=\"\" width=\"280\" height=\"280\" /></p>\r\n<p>&nbsp;<br /><br /><br />И ретро лампа Sun lumen - 299&nbsp;руб. !<br /><br /><a href=\"../products/lampa-nakalivaniya-e27-40w-kolba-zolotaya-051-910a\">Ретро лампа Sun lumen</a><br /><br /><img src=\"../files/uploads/sun-lumen_056-755.1024x768w.jpeg\" alt=\"\" width=\"275\" height=\"275\" /><br /><br /><br /><br /><br />Если же Вас интересует что-то посерьезнее, то добро пожаловать в наш раздел - <a title=\"Светильники лофт\" href=\"../catalog/svetilniki-loft\">Светильники лофт<br /><br /><br /><br /><img src=\"../files/uploads/Industrial_edison_chandelier_retro_1.jpg\" alt=\"\" width=\"286\" height=\"286\" /><br /></a></p>', 0),
(6, 'Российские люстры от бренда Аврора!', 'rossijskie-lyustry-ot-brenda-avrora', '', 0, 'Российские люстры от бренда Аврора!', 'Люстры Аврора', 'Люстры Аврора отзывы, обзоры', 'Дорогие гости! В нашем интернет магазине представлен Российски бренд Аврора, выпускающий чудесные люстры.не уступающие по качеству европейским собратьям.', 'Предлагаем Вашему вниманию чудесный видео ролик от данного производителя, демонстрирующий весь цикл производства люстры под красивый ambient трэкс с замечательной операторской работой.<br /><br /><iframe src=\"https://www.youtube.com/embed/XSZhfkDi3U8\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe><br /><br /><br />И сама ссылка на люстры бренда Аврора, которые вы можете купить в нашем интернет магазине со скидкой с первого заказа!<br /><br />Добро пожаловать заказывать!<br /><br /><a href=\"../catalog/lyustry-avrora\">Люстры Аврора<br /><br /></a><br /><br />', 0),
(7, 'О том как я optipng на shared hosting fozzy ставил', 'o-tom-kak-ya-optipng-na-shared-hosting-fozzy-stavil', '', 0, 'О том как я optipng на shared hosting fozzy ставил', 'О том как я optipng на shared hosting fozzy ставил', 'ПРЕДИСЛОВИЕ \r\nПривет, читатели. Это мой первый пост на хабре. Нахожусь в приподнятом настроении, поэтому решил поделиться знанием с миром ну и для себя оставить, когда через месяц сложно будет вспомнить последовательность действий. Частенько мне приходило', 'Частенько мне приходилось уже изобретенное колесо изобретать снова. Получалось быстрее, тем не менее, мне это стоило обычно недюжинного напряжения ума. Многие, как и мы, хостят свои сайты на т.н. shared hosting. Наш хостер fozzy всем хорош, менять его желания никакого нет, поэтому, когда в google adwords поддержке нас ткнули носом в низкий рейтинг pagespeed insight на мобильной версии, пришлось снова вернуться к вопросу оптимизации изображений. Несколько месяцев назад этот вопрос уже возникал. Я пытался по всяким мануалам, в том числе с хабра, добиться высокого рейтинга на pagepseed, но одно дело получить 100 баллов при загрузке страницы Hello world!и совсем другое дело получить высокую оценку на странице интернет магазина, на котором куча скриптов, которые нельзя отключить или загружать асинхронно. В общем оптимизация тогда закончилась на том, что из невыполненного осталось только сжатие изображений.', '<h3>ПРЕДИСЛОВИЕ</h3>\r\nЧастенько мне приходилось уже изобретенное колесо изобретать снова. Получалось быстрее, тем не менее, мне это стоило обычно недюжинного напряжения ума. Многие, как и мы, хостят свои сайты на т.н. shared hosting. Наш хостер fozzy всем хорош, менять его желания никакого нет, поэтому, когда в google adwords поддержке нас ткнули носом в низкий рейтинг pagespeed insight на мобильной версии, пришлось снова вернуться к вопросу оптимизации изображений. Несколько месяцев назад этот вопрос уже возникал. Я пытался по всяким мануалам, в том числе с хабра, добиться высокого рейтинга на pagepseed, но одно дело получить 100 баллов при загрузке страницы Hello world!и совсем другое дело получить высокую оценку на странице интернет магазина, на котором куча скриптов, которые нельзя отключить или загружать асинхронно. В общем оптимизация тогда закончилась на том, что из невыполненного осталось только сжатие изображений. Остальные пункты или были выполнены или их просто невозможно было выполнить не обрезая функциональность. Те из Вас, кто интересовался вопросом оптимизации изображений, наверняка знают, что библиотеки PHP для работы с растровым изображением, что imagick, что GD, при любых настройках сжатия не позволяют получить изображения, которые гугл не сможет сжать еще сильнее. Тогда искания пришли именно к тому, что после генерации растрового изображения средствами php, нужно его сжимать чем-то еще. Тогда эти Optipng и jpeglib появились на горизонте. Спросил поддержку о возможности использования этих программ на shared hosting, сказали - нельзя. Потыкался, потыкался и плюнул. Мне ленивому и очень гордому показалось унизительной и просто неприемлемой перспектива грузить на сайт 400к картинок предварительно сжатых во всех нужных разрешениях. Ведь я пользуюсь ПК еще со времен ДОСа, а в сеть ходил еще во времена FIDONET по внутреннему модему на скорости 9600 бод. Тогда я решил, если не могу сделать красиво, то вообще не буду делать. Прошли месяцы. Очередной подход к снаряду увенчался успехом. Все дело в настрое, я просто решил, что должен найти способ. Тем более, что в англоязычном тырнете писали об установке отдельных программ на shared hosting. Про fozzy ничего не нашел, зато нашел, как кто-то на dreamhost optipng 0.7.4 поставил. Получилось как в той сказке про лягушек в ведре с молоком. Одна устала барахтаться и утонула, а другая не ожидая чего-то определенного, продолжала и продолжала. Вспоминая опыт успешной сборки драйвера для своего китайфона philips xenium на андроиде, не имея исходников прошивки, я продолжал и продолжал. Делюсь результатом с Вами. Вот восстановленная по памяти последовательность событий рабочего варианта установки на виртуальных хостинг fozzy optipng 0.7.6\r\n<h3>ЖУРНАЛ СОБЫТИЙ</h3>\r\n<ol>\r\n<li>Смотрю версию системы: cat /proc/version Показывает, что у нас Red Hat 4.4.7-4, а именно:\r\n<blockquote>\"Linux version 2.6.32-531.23.3.lve1.2.66.el6.x86_64 (mockbuild@koji.cloudlinux.com) (gcc version 4.4.7 20120313 (Red Hat 4.4.7-4) (GCC) ) #1 SMP Fri Sep 12 10:57:40 EDT 2014\"</blockquote>\r\n</li>\r\n<li>Ставлю на VirtualBox centOS 7 x86_64.</li>\r\n<li>Ставлю нужные для компиляции optipng пакеты. Я выбрал при установке минимальный набор, поэтому после установки мне пришлось поставить gcc и mc. Делаем так: yum install mc gcc</li>\r\n<li>Я не создавал пользователей и сидел рутом. Создаем каталог для работы mkdir ~/usr Переходим туда: cd ~/usr Качаем libpng-0.7.6.tar.xz wget http://prdownloads.sourceforge.net/optipng/optipng-0.7.6.tar.gz</li>\r\n<li>Распаковываем: tar -xzvf optipng-0.7.6.tar.gz Переходим в созданный каталог: cd optipng-0.7.6 Запускаем configure для установки в указанное место: ./configure &ndash;prefix=/root/usr/ Компилируем: make Устанавливаем: make install В результате манипуляций в каталоге /root/usr/bin у нас должен лежать бинарник optipng</li>\r\n<li>Заставить работать его на fozzy сразу не удалось. Запуск приложения давал это:\r\n<blockquote>./optipng: /lib64/libc.so.6: version `GLIBC_2.14\' not found (required by ./optipng)</blockquote>\r\n</li>\r\n<li>Подходящая нам версия GLIBC есть в этом пакете:\r\n<blockquote>ftp://rpmfind.net/linux/opensuse/distribution/12.1/repo/oss/suse/x86_64/glibc-2.14.1-14.12.5.x86_64.rpm</blockquote>\r\n</li>\r\n<li>Качаем и распаковываем. На хостинге приложения для работы с rpm нет, поэтому я все это делал на моей виртуалке centos.</li>\r\n<li>Создаем рабочий каталог: mkdir ~/usr/glibc</li>\r\n<li>Переходим в рабочий каталог: cd ~/usr/glibc</li>\r\n<li>Качаем файл: wget ftp://rpmfind.net/linux/opensuse/distribution/12.1/repo/oss/suse/x86_64/glibc-2.14.1-14.12.5.x86_64.rpm Распаковываем: rpm2cpio glibc-2.14.1-14.12.5.x86_64.rpm | cpio -idmv Теперь в каталоге lib64 у нас есть нужные библиотеки. Я забрал весь каталог целиком.</li>\r\n<li>Для работы optipng на хостинге нам нужно следующее: ./bin/optipng <strong>сама программа</strong> ./lib64/ <strong>библиотеки glibc 2.14</strong></li>\r\n<li>Запиливаем все это на хостинг и распаковываем. Я запускал все из рабочей директории: Идем в каталог с программой: cd ~/bin Запускаем: LD_LIBRARY_PATH=~/lib64 ./optipng</li>\r\n<li>Вуаля! <img src=\"http://image.prntscr.com/image/9ba83fdd54484c8fad6d689dc8074651.jpg\" alt=\"image\" /></li>\r\n</ol>\r\nПробуем сжать retro_after.png: LD_LIBRARY_PATH=~/lib64 ./optipng ~/www/retro_after.png\r\n<blockquote>** Processing: retro_after.png 800x800 pixels, 3x8 bits/pixel, RGB Input IDAT size = 284863 bytes Input file size = 285328 bytes Trying: zc = 9 zm = 8 zs = 0 f = 0 IDAT size = 282260 zc = 9 zm = 8 zs = 0 f = 5 IDAT size = 192148 zc = 9 zm = 8 zs = 1 f = 5 IDAT size = 189198 Selecting parameters: zc = 9 zm = 8 zs = 1 f = 5 IDAT size = 189198 Output IDAT size = 189198 bytes (95665 bytes decrease) Output file size = 189255 bytes (96073 bytes = 33.67% decrease)</blockquote>\r\nВот несжатый файл: https://sevenlight.ru/retro_before.png Размер: 285 328 байт <br /><br /><img src=\"../retro_before.png\" alt=\"image\" /> <br /><br />А вот сжатый: https://sevenlight.ru/retro_after.png Размер: 189 255 байт<br /> <img src=\"../retro_after.png\" alt=\"image\" /> <br />Кому лень заморачиваться, вот готовый архив для загрузки на хостинг. https://sevenlight.ru/optipng-fozzy.tar.gz', 0),
(8, 'Натяжной потолок. Какую люстру выбрать?', 'natyazhnoj-potolok-kakuyu-lyustru-vybrat', '9072b48ad42efdd7f991aae86aa1d307.jpg', 41, 'Натяжной потолок. Какую люстру выбрать?', 'Натяжной потолок. Какую люстру выбрать?', 'Основные рекомендации по подбору люстры под натяжной потолок', '<p>В этой статье Вы озакомитесь с общими рекомендациями, узнаете важные нюансы о том, как следует выбирать люстры для натяжного потолка, какие типы лампочек подходят лучше и на что нужно обратить внимание при подборе люстры для натяжного потолка</p>', '<h1>Люстра для натяжного потолка - как выбирать</h1><p>В этой статье Вы озакомитесь с общими рекомендациями, узнаете важные нюансы о том, как следует выбирать люстры для натяжного потолка, какие типы лампочек подходят лучше и на что нужно обратить внимание при подборе люстры для натяжного потолка.</p><p>Говоря просто, практически любую люстру можно подвесить к натяжному потолку и вы не должны сковывать себя какими-то ограничениями, но нужно следовать определенным правилам, чтобы соблюдать технику безопасности. Мы специально подготовили раздер <a href=\"/catalog/lyustry-dlya-natyazhnyh-potolkov\">Люстры для натяжного потолка</a>, в этом разделе собраны основные люстры, представленные на нашем сайте. С помощью фильтров Вы можете сортировать люстры по множеству параметров. Основные из них это стиль, бренд, цена, виды материалов.</p><p>Всё чаще люди стали монтировать в квартирах натяжные потолки из ткани или плёнки ПВХ. Кроме того, что это модно и красиво, такое покрытие ещё и практично. Решив установить натяжной потолок, необходимо задуматься и о смене осветительного прибора, ведь не каждый подойдёт к такой конструкции. Какую же люстру выбрать, на что обращать внимание при покупке? Эти вопросы часто задают потребители, решившие сменить потолочное покрытие. Ведь только правильно подобранный светильник подчеркнёт изысканность и изящество натяжного тканевого или плёночного материала.</p><p>Люстру следует выбирать перед начальным этапом ремонтных работ, ведь проводку укладывают заранее. Кроме того, до монтажа натяжного потолка устанавливают и крепление для светильника, так как прибор прикручивают не на полотно, а на основание.</p><p><img src=\"/img/blog/9072b48ad42efdd7f991aae86aa1d307.jpg\" alt=\"Люстра для натяжного потолка\" title=\"Схема монтажа люстры под натяжной потолок\" /></p><p><strong>Способ крепления на крюк и способ крепления на планку</strong></p><p><img src=\"/img/blog/be203a88c7352506e5436b06841ba546.jpg\" alt=\"Схема крепления люстры под натяжной потолок\" title=\"Схема крепления люстры под натяжной потолок\" /></p><p>Люстра не должна повредить покрытие, ведь оно при нагревании может изменить цвет и деформироваться. Особенность натяжного потолка требует соблюдать некоторые ограничения при установке приборов.</p><p>Также обращайте внимание, чтобы на светильниках не было деталей с острыми краями, которые могут повредить полотно при монтаже.</p><h2>Выбор люстры для натяжного потолка</h2><p>Навесной потолок это тканевое полотно или плёнка. Его монтируют методом растяжки, закрепляя по периметру разными способами. При нагревании материала выше 60 он теряет свойства начинает желтеть, изменять размер, форму и конфигурацию. Выбор люстры для натяжного потолка это в первую очередь предпочтение того вида модели, который будет максимально безопасным для покрытия. Это может быть как потолочная люстра с видами которой вы можете ознакомиться в нашем разделе <a href=\"/catalog/lyustry_potolochnye\">Потолочные люстры</a> или же подвесная люстра - <a href=\"/catalog/lyustry_potolochnye\">Подвесные люстры</a></p><h3>Зависимость выбора от расположения и вида лампочек</h3><p>При подключении галогенных ламп дополнительно устанавливают преобразователи питания. Они выделяют большое количество тепла. Приборы не рекомендуют монтировать между основным потолком и полотном, так как там практически отсутствует вытяжка. Из-за этого устройства перегреваются и быстро приходят в негодность. Устанавливать преобразователи лучше в вентилируемой нише, а над натяжным потолком проложить провода. <strong>Современные светодиодные лампочки - отличная замена галогенным. Они не греются, потребляют в 5 раз меньше энегрии и не требуют наличия трансформатора, а подключаются напрямую к 220 в.</strong></p><p>Чтобы не допустить перегрева полотна, из-за чего на нём могут появиться чёрные круги, лучшими вариантами моделей будут люстры с рожками, которые направлены вниз. Когда же у вас установлен глянцевый потолок, можно выбрать светильник с лампами, смотрящими вверх, так как из-за отражательного эффекта их требуемое количество уменьшается вдвое, то есть вместо шести штук будет достаточно трёх. <strong>Но необходимо учитывать, чтобы лампы не выделяли много тепла. Тут на помощь придут опять светодиодные лампочки. На сегодняшний день на современном рынке представлены все типы светодиодных ламп под любой цоколь. По этому просто при копупке люстры под натяжной потолок просите менеджера укомплектовать люстру светодиодными лампочками.</strong></p><p>При выборе накладных потолочных светильников следует уделять внимание наличию специальной нейтрализационной пластины у основания, которая не даст нагреться натяжному потолку. Кроме того, желательно, чтобы и сам корпус был не из металла, так как при включённых лампочках он нагревается, что опасно как для тканевого, так и для плёночного полотна. Как правило, современные производители люстр учитывают этот фактор и тут беспокоится не о чем.</p><p>Выбирая люстру для натяжного потолка, отдайте предпочтение светильнику, имеющему много светодиодов с маленькой мощностью, чем устройству с несколькими большими лампами. Лучший вариант, чтобы они были в закрытых колпаках.</p><p><img src=\"/img/blog/8b0bdbcffaa3b100353b5dadcdf9eab9.png\" alt=\"Люстра потолочная Eurosvet\" title=\"Люстра потолочная Eurosvet\" /></p><h3>Виды ламп</h3><p>Для люстр, устанавливаемых на натяжном полотне, применяют любые типы ламп. Только вот на некоторые виды есть определённые ограничения:</p><ul><li>1. Лампы накаливания самый неподходящий вариант. Они не столько светят, сколько греют. В крайнем случае можно в люстру вкручивать лампу, мощность которой не более 60 Вт. Однако расстояние от неё до натяжного полотна не должно быть менее 25 см. Если же рожки смотрят вверх, удалять их от потолка следует на 40 см.</li><li>2. Галогенные лампы хороши тем, что их свет близок к естественному, но греются они так же, как и первый вид. В зависимости от мощности их тоже используют в люстрах, рожки которых находятся на удалении от натяжного полотна.</li><li>3. Энергосберегающие хороший вариант. Такие лампы нагреваются слабо, поэтому допускается их близкое расположение к потолку. Но этот вид требует к себе особо осторожного отношения, так как внутри находится ртутная альмагама (вещество первого класса опасности). Кроме того, энергосберегающие лампы нельзя использовать в помещении с повышенной влажностью.</li><li>4. <strong>Светодиодные лучший вариант для светильников, установленных на натяжном потолке. При высоком коэффициенте полезного действия минимальный нагрев. При этом лампы имеют компактный размер и большой ресурс.</strong></li></ul><h3>Виды светильников</h3><p>Видов светильников для натяжного потолка не так уж и много. Это накладные, подвесные и встраиваемые. Выбор зависит от концепта дизайн-проекта помещения, ну и от стоимости.</p><p>Светильники накладные стоят дешевле встраиваемых, монтировать их легче, но и мощность меньше. В основном их применяют в качестве дополнительной подсветки или в декоративных целях. Также данные типы светильников могут комбирнироваться. По периметру могут идти встраиваемые светильники а в центре может быть накладной или подвесной светильник.</p><p><img src=\"/img/blog/1834cd9a3eca3364d162f195b2f871e7.jpg\" alt=\"Люстра потолочная в интерьере\" title=\"Люстра потолочная в интерьере\" /></p><p>Для установки встраиваемых приборов необходим промежуток между потолком и полотном. Если люстра висит на крюке на длинной цепочке, в рожки можно вкручивать более мощные лампы. Ввиду того что они находятся на достаточном расстоянии, полотну вреда не нанесут.</p><p>При покупке светильника обращайте внимание на тип крепления. Люстру вешают одним из трёх способов: на потолочный крюк, монтажную рейку или крестообразную пластину.</p><h3>Несколько советов от дизайнера</h3><p>Люстра, прежде всего, должна дополнять красоту потолочного покрытия, а не затмевать его. Поэтому не выбирайте шикарный прибор, при котором прелесть полотна уйдёт на второй план.</p><p>Выбирая светильник, советуйтесь с менеджерами в магазине. Без совета специалистов есть вероятность приобрести люстру со многими ненужными функциями и деталями, которые впоследствии вам не пригодятся. А это лишняя трата денег.</p>', 1),
(9, 'Люстры в стиле флористика', 'lyustry-v-stile-floristika', '437e944bfff1c5917a1427f3f556a02f.jpeg', 39, 'Люстры в стиле флористика', 'Люстры в стиле флористика', 'Люстры в стиле флористика. Краткий обзор, подборка люстр в стиле флористика', '<p>Внешний вид любого помещения зависит от разных факторов, в том числе от центрального источника света. Удачным выбором может оказаться люстра в стиле флористика, которая гармонирует практически с любой обстановкой. В этой статье мы расскажем о таком популярном виде люстр, как цветочные люcтры. Вы узнаете, почему данные люстры так популярны и сможете выбрать из нашего каталога &ndash; <a href=\"/catalog/lyustry-floristika\">Люстры в стиле флористика</a>, понравившуюся Вам люстру-цветок!</p>', '<h1>Люстры в стиле флористика - обзор</h1><p>Внешний вид любого помещения зависит от разных факторов, в том числе от центрального источника света. Удачным выбором может оказаться люстра в стиле флористика, которая гармонирует практически с любой обстановкой. В этой статье мы расскажем о таком популярном виде люстр, как цветочные люcтры. Вы узнаете, почему данные люстры так популярны и сможете выбрать из нашего каталога <a href=\"/catalog/lyustry-floristika\">Люстры в стиле флористика</a>, понравившуюся Вам люстру-цветок!</p><h2>Немного истории</h2><p>Цветочные светильники нельзя считать новым словом в дизайне интерьеров. Впервые они появились еще в средневековой Англии. В богатых домах это были массивные бронзовые конструкции с причудливыми флористическими орнаментами. А бедняки сами сушили и собирали растения, украшая ими свои простенькие светильники.</p><p>Так люстры в стиле флористика стали распространяться по всему миру. Они олицетворяли нежность и свежесть весны. Всевозможные цветы разных оттенков украшали абажуры и сплетались в чудесные композиции.</p><p><a href=\"/products/potolochnaya-lyustra-avrora-florentsiya-10115-3c\"> <img src=\"/img/blog/437e944bfff1c5917a1427f3f556a02f.jpeg\" alt=\"Люстра в стиле флористика\" title=\"Люстра в стиле флористика\" /> </a></p><h2>Трендовая современность</h2><p>Светильники от богини Флоры актуальны и сейчас. Обычно они представляют собой плафоны в виде соцветия-тюльпана, иногда с затейливым стеблем и листьями, или композиции из мелких цветочков со светодиодными лампами. Такие люстры часто становятся фантазийной копией настоящих цветов со всем богатством форм, оттенков и изгибов. Порой их даже не отличишь от живых: настолько велико сходство.</p><p><a href=\"/products/potolochnaya-lyustra-mw-light-buket-421013406\"> <img src=\"/img/blog/10bd6921f37d38228ca5aa9e5d24654f.png\" alt=\"Люстра в стиле флористика\" title=\"Люстра в стиле флористика\" /> </a></p><p>Мастера сочетают разноцветные плафоны-бутоны с декоративными листиками и веточками, создавая настоящие произведения искусства. Особенно популярны люстры в форме розы, колокольчиков, весенних тюльпанов. В тренде сейчас и необычные люстры с цветочными принтами. Они могут быть выполнены из пластика, ткани, фарфора, стекла. Некоторые их них сделаны вручную, поэтому являются уникальными.</p><p>Цвета используются преимущественно мягкие, природные. Например, бежевый, сиреневый, серебристый, голубой. Любители смелых решений могут выбрать алые, терракотовые, фиолетовые оттенки.</p><h2>Достойное украшение интерьера</h2><p>Цветочные люстры универсальны и функциональны. Они уместны в любых видах жилых помещений, за исключением, пожалуй, ванных комнат. В спальне такие светильники создадут романтическое настроение, гостиную украсят, кухне подарят уют, а в детской будет царить сказочная атмосфера. Они подходят для интерьерных решений, выполненных в разных стилях. Флористические люстры ярко и равномерно освещают пространство, оживляя помещение и придавая ему особую эстетику.</p><p>Лучше всего цветочные люстры сочетаются с вечной и неувядающей классикой. Вполне можно органично вписать их в дизайн прованс, бохо, кантри, если подобрать светильник из натуральных материалов. Даже с модерном не будет принципиальных расхождений. А вот со стилем хай-тек они имеют мало общего, хотя, если постараться, можно подобрать альтернативные варианты, например, фантастические растения из проволоки или хрустальные шары в форме огромных соцветий.</p><p>Люстры в стиле флористика отличное сочетание гармонии природных линий и функциональности современных технологий.</p>', 1),
(10, 'Светильник в коридор', 'osvetitelnye-pribory-dlya-prihozhej-kakie-vybrat', 'b38058728c43584af3ecb6cac106af59.jpg', 35, 'Светильник в коридор - как выбрать.', 'Светильник в коридор', 'Основные рекомендации по светильникам в коридор', '<p>В магазинах осветительной техники представлен выбор самых разнообразных приборов: от стеклянных или пластиковых абажуров до роскошных навесных люстр. Правда, разрабатывают их индивидуально для определённого помещения, соответственно, светильник, спроектированный для гостиной, не подойдёт для украшения интерьера прихожей.</p>', '<h1>Светильники в коридор - как выбрать</h1><p>В магазинах осветительной техники представлен выбор самых разнообразных приборов: от стеклянных или пластиковых абажуров до роскошных навесных люстр. Правда, разрабатывают их индивидуально для определённого помещения, соответственно, светильник, спроектированный для гостиной, не подойдёт для украшения интерьера прихожей.</p><p>Коридор имеет самую маленькую площадь. Освещение в нём организовать непросто. Ввиду небольшого размера прихожей следует с ответственностью подходить к выбору светильника. Ведь только правильно подобранные устройства обеспечат в небольшом помещении комфорт и удобство, а также украсят интерьер.</p><p><img src=\"/img/blog/b38058728c43584af3ecb6cac106af59.jpg\" alt=\"Светильни встраиваемые в коридор\" title=\"Светильни встраиваемые в коридор\" /></p><h2>Виды осветительных приборов</h2><p>Люстры для прихожих выбирают без особых изяществ. Обычно это модели нейтрального дизайна без украшений. Светильники для коридора делятся на два типа: потолочные или настенные. Реже применяется вариант расположения осветительного прибора на полу. Но такой вид не может быть основным источником света. Он применяется в качестве дополнения.</p><p>В прихожей чаще всё же применяют потолочную стандартную люстру. Такой вид подходит для большинства коридоров. Но для помещения с невысокими потолками не подойдут большие витиеватые люстры на длинных цепях. В таких случаях лучше применять споты, которые позволяют изменять направление потока света.</p><p><img src=\"/img/blog/59b6f20401faa7d895e1ac227beecd61.jpg\" alt=\"Потолочный светильник в коридор\" title=\"Потолочный светильник в коридор\" /></p><p>Если в прихожей низкие потолки, наилучший вариант потолочный светильник. Грамотно подобраный потолочный светильник будет давать достаточно света и может стать центровым элементом дизайна в прихожей. Но такого эффекта можно добиться если светильник будет подходящих размеров и стилистически будет вписываться в интерер Вашей прихожей.</p><p><img src=\"/img/blog/58e1347afb8b2d2be707f327a0536e44.jpg\" alt=\"Потолочный светильник в коридор\" title=\"Потолочный светильник в коридор\" /></p><h2>На какие характеристики обращать внимание</h2><p>Главный критерий, чтобы потолочные светильники гармонировали с единым интерьером прихожей. Кроме этого, при выборе уделяйте внимание следующим параметрам:</p><div style=\"margin-left: 2em;\"><ul><li>Режим освещения.</li><li>Экономия электроэнергии (как правило, светильник всегда можно укомплектовать светодиодными лампами</li><li>Вид патронов. На этот критерий нужно обращать особое внимание. Для некоторых типов очень трудно найти лампочки с нестандартным цоколем. Кроме того, патроны изготавливают из разных материалов. Они могут быть из карболита, керамики или термостойкого пластика.</li><li>Количество лампочек. Этот параметр следует выбирать исходя не от размера светильника, а от площади прихожей. В среднем на 1 кв. м. нужно 20 ватт света</li><li>Форма коридора. Если это помещение квадратной конфигурации, можно повесить одну люстру в центре потолка и дополнительные источники света по бокам. Для узкой и продолговатой прихожей выбирают длинные модели светильников.</li></ul></div><h2>Настенные светильники</h2><p>При оформлении коридора вместо потолочных люстр и светильников часто применяют настенные светильники. При небольшом размере помещения с этими приборами можно получить интересную игру света, что, безусловно, лучшим образом отразится на интерьере. Для создания более гармоничного сочетания можно пригласить дизайнера, который поможет преобразить даже самую невыразительную прихожую.</p><p>При изготовлении светильников, которые крепятся к вертикальной поверхности стены, используют разные материалы. Среди них можно выделить хрусталь, декоративное стекло, светорассеивающий пластик, текстиль. Из этих материалов получаются качественные, но в то же время недорогие светильники.</p><h5>Связь формы люстры и размера прихожей</h5><p>До коридора из комнаты не доходит естественное освещение, потому одного маленького светильника в этом помещения не хватит. Можно, конечно, смонтировать дополнительные источники света. Это могут быть встраиваемые светильники. Они вполне смогут осветить все пространство прихожей. Но такой тип светильниках предусмотрен в случае, если у вас натяжной или гипсокартонный потолок.</p><p>Если размеры потолков до 2,5 метров, то тут нужно выбирать светильник или люстру с подвесом не более 50 см возможно, стоит рассмотреть потолочный вариант люстры или светильника, когда нету никакого подвеса и источник света крепится непосредственно основанием к потолку или же источник света находится на небольшой штанге, до полуметра. Это базовые рекомендации, которые должны Вас обезопасить от неправильного распределения освещения в прихожей. Надеемся Вы найдете свой светильник, который гармонично впишется в Ваш интерьер и прослужит Вам ни один год</p><p><img src=\"/img/blog/8fed8446cdd8a7099ffe2b8132c0c726.jpg\" alt=\"Потолочный светильник в коридор\" title=\"Потолочный светильник в коридор\" /></p>', 1);
INSERT INTO `s_blog` (`id`, `name`, `trans`, `image`, `image_id`, `meta_title`, `meta_keywords`, `meta_description`, `annotation`, `text`, `visible`) VALUES
(12, 'Где купить люстру? Почему стоит покупать люстру в интернет магазине?', 'gde-kupit-lyustru', '0603c7f9eff97134f937497cc161cf6e.jpg', 33, 'Где купить люстру? Почему стоит покупать люстру в интернет магазине?', 'Где купить люстру? Почему стоит покупать люстру в интернет магазине?', 'Где купить люстру?, почему удобно покупать люстры в интернет-магазине? Мы расскажем Вам об этом.', '<p>В процессе создания эстетически привлекательного и современного облика в доме немаловажное значение имеет выбор осветительных приборов. Сейчас нет никаких проблем с поиском места, где купить люстру. В специализированных магазинах можно найти огромный ассортимент функциональных, модных, стильных и оригинальных светильников на любой вкус для решения любых задач по организации освещения в помещениях разного назначения.</p>', '<h1>Где купить люстру?</h1><p>В процессе создания эстетически привлекательного и современного облика в доме немаловажное значение имеет выбор осветительных приборов. Сейчас нет никаких проблем с поиском места, где купить люстру. В специализированных магазинах можно найти огромный ассортимент функциональных, модных, стильных и оригинальных светильников на любой вкус для решения любых задач по организации освещения в помещениях разного назначения.</p><p>Следует отметить, что зачастую большое количество торговых центров с однотипной продукцией ставит покупателей в тупик. Ведь для того, чтобы приобрести подходящую люстру мало заранее рассмотреть и изучить популярные тенденции. Покупателю приходится часами колесить от одной торговой точки до другой, делая для себя какие-то пометки о понравившемся товаре и надеясь, что в следующем магазине он найдет именно то, о чем он мечтает. Сложно оценить все имеющиеся на рынке предложения, чтобы подобрать для своего интерьера именно тот вариант, который наиболее точно будет отвечать вашим потребностям и идеально впишется в дизайн помещения.</p><p><img src=\"/img/blog/0603c7f9eff97134f937497cc161cf6e.jpg\" alt=\"Купить люстру в интернет магазине\" title=\"Люстры интернет магазин\" /></p><p>Поэтому одним из самых удобных, эффективных и выгодных способов купить люстру в интернет магазине. Продажа онлайн сейчас приобретает все более массовый характер благодаря своим преимуществам перед магазинами обычного типа. С помощью удобных каталогов светотехнической продукции можно не выходя из дома просмотреть все товары, ознакомиться с их характеристиками, отзывами покупателей и тут же сравнить наиболее понравившиеся варианты как по цене и дизайнерскому решению, так и по своему функционалу.</p><p>Достоинства интернет-магазина очевидны. Это не только экономия времени и сил, но и возможность в спокойной обстановке осмотреть все предложения. Благодаря структурированным каталогам, где продукция распределена по стоимости, типам, производителям и другим критериям, поиск нужного предмета станет простым и приятным делом, которое не отнимет у вас много времени.</p><p><img src=\"/img/blog/7f315c67758a4e0ed0ed374b86ff328c.jpg\" alt=\"Купить люстру в интернет магазине\" title=\"Люстры интернет магазин\" /></p><p>Помимо этого, одним из немаловажных преимуществ интернет-магазина будут и более низкие цены на продукцию. Ведь для работы виртуального шопинг-центра не нужен целый штат сотрудников, продавцов, консультантов и кассиров, а также отпадает необходимость в аренде или покупке дорогостоящих торговых площадей, не нужно оплачивать огромные счета за электроэнергию, охрану, услуги инкассаторов и т.д. Именно за счет оптимизации расходной части, ценообразование в онлайн-магазинах всегда на стороне покупателей.</p><p>Ни в одном традиционном торговом центре невозможно представить даже малую часть того ассортимента, который может себе позволить интернет-магазин. Многие товарные позиции и вовсе можно найти только в онлайн-каталогах. Ограниченность выставочных помещений в обычных торговых точках делает интернет-магазины более привлекательными и функциональными, давая клиентам все условия для принятия обдуманных и взвешенных решений перед покупкой.</p><p>Не все люди обладают возможностями ходить по магазинам. Кто-то не может в силу своего здоровья, кто-то постоянно занят работой и своими личными делами, а кто-то просто не любитель классического шопинга. Во всех этих случаях на помощь придет удобная услуга доставки покупок до дома. Курьерские службы, транспортные компании, почтовые сервисы сейчас есть много способов получать покупки из интернет-магазинов прямо на дом. Популярность и востребованность этих услуг стали залогом качественной и быстрой доставки товаров для покупателей по всей стране.</p><p>Многие из нас стараются идти в ногу со временем, они тщательно относятся к выбору тех вещей, которыми собираются наполнить свой быт. Сейчас одним из самых важных критериев в жизни многих людей является время. Стремительный ритм жизни в современном обществе съедает минуты, часы и даже дни на выполнение не самых важных дел в жизни. Бережно относясь к этому бесценному ресурсу, современные люди стараются посвящать его себе и своим близким, поэтому сейчас в обществе так стремительно развивается предоставление услуг через интернет.</p><p>Благодаря современным технологиям и всеохватности мобильного интернета мы получаем возможность быть постоянно онлайн. С помощью ноутбука, планшета или смартфона можно посетить интернет-магазин в метро, автобусе, поезде и сделать покупку, не тратя на это лишние драгоценные часы. Недаром в одной из самых высокотехнологичных стран мира Японии виртуальные витрины продуктовых супермаркетов расположены прямо на станциях метро. Пока люди в ожидании поезда стоят на платформе, они могут выбрать себе необходимые товары, а когда приедут домой, то служба доставки вскоре уже привезет все заказанное. Существенная экономия времени очевидна.</p><p>Наш интернет-магазин светильников Sevenlight.ru один из лидеров рынка в своем сегменте. Кроме внушительного ассортимента светотехнических товаров, мы предлагаем профессиональное обслуживание, грамотные консультации, техническую поддержку. Многолетний опыт работы позволил нам наладить работу интернет-магазина на самом высшем уровне. У нас можно не просто заказать люстру, бра или настольную лампу, а получить полноценное обслуживание на всех этапах, начиная от помощи при выборе товара, оформлении доставки и, заканчивая, гарантийным и постгарантийным сервисом.</p><p>Нашими главными критериями является серьезный подход к работе и внимательное отношение к покупателям. Все товары проходят техническую экспертизу перед отправкой, что гарантирует качественную работу светотехнической продукции. Фотографии товаров и их габаритные размеры полностью соответствуют оригинальным изделиям, поэтому вы можете не волноваться, что купить люстру в интернет-магазине это не значит стать обладателем кота в мешке.</p><p>Даже тем, кто любит перед покупкой пощупать товар вживую, переживать не стоит. Качественные изображения в каталогах, подробные характеристики и описания светильников дадут вам исчерпывающую информацию для принятия решения о покупке. Судя по нашему опыту, процент покупателей, которых не устраивает внешний вид изделия, близок к нулю, и это не взятые с потолка цифры, а реальные факты. Мы смогли достичь таких показателей благодаря четко отлаженной работе и наличию максимально подробных технических данных о каждой товарной позиции.</p><p>Мы предлагаем оперативную доставку во все регионы России. Клиентов на каждом этапе сопровождают личные менеджеры, которые помогут решить все возникающие вопросы начиная от выбора моделей и заканчивая монтажными работами. Индивидуальный подход ко всем покупателям дает возможность достичь полного взаимопонимания и получить взаимовыгодные результаты.</p><p>Интернет-магазин Sevenlight.ru работает напрямую с крупнейшими мировыми производителями, например, с компанией <a href=\"/catalog/lyustry-eurosvet\" target=\"_blank\">Eurosvet</a>, поэтому мы можем предложить самые низкие цены на люстры. Кроме того, у нас работает система скидок для постоянных покупателей и оптовиков. Сотрудничество с нашим интернет-магазином будет выгодно для всех! Если у вас еще остались вопросы, где купить люстру, то звоните нам, и мы развеем все ваши сомнения!</p>', 1),
(13, 'Люстры для кухни. Какую купить люстру на кухню?', 'lyustry-dlya-kuhni-kakuyu-kupit-lyustru-na-kuhnyu', '4a0b3fcc6265dfc63919c36401c15318.jpg', 30, 'Люстры для кухни Какую купить люстру на кухню?', 'Люстры для кухни Какую купить люстру на кухню?', 'О люстрах для кухни. Какую люстру купить на кухню. Выбор люстры для кухни', '<p>Изящная люстра &ndash; не тривиальный, но весьма эффективный способ внести в интерьер кухни оригинальную изюминку. Правильно подобранные <a href=\"/catalog/lyustry-dlya-kuhni\" target=\"_blank\">люстры для кухни</a> станут отправной точкой, отталкиваясь от которой вы создадите уникальный дизайн. С помощью люстры легко сформировать эксклюзивный, стильный и яркий внешний вид кухни.</p>', '<h1>Люстры для кухни</h1><p>Изящная люстра не тривиальный, но весьма эффективный способ внести в интерьер кухни оригинальную изюминку. Правильно подобранные <a href=\"/catalog/lyustry-dlya-kuhni\" target=\"_blank\">люстры для кухни</a> станут отправной точкой, отталкиваясь от которой вы создадите уникальный дизайн. С помощью люстры легко сформировать эксклюзивный, стильный и яркий внешний вид кухни.</p><p>Чтобы не ошибиться при выборе этого важного предмета интерьера для кухни или столовой, нужно учитывать несколько существенных критериев. Как известно, люстра представляет собой подвесной светильник со встроенными лампочками. Именно это и отличает данный тип светотехнических приборов от других разновидностей.</p><p>Вначале нужно решить, какую функцию будет выполнять люстра. Подвесные люстры для кухни могут выполнять функцию основного источника света или же их главная задача стать одним из декоративных элементов интерьера. Все зависит от параметров кухни, возможностей и предпочтений заказчиков.</p><p>Главным критерием при выборе люстры является стилистическое направление в дизайне вашей кухни. Поэтому первым делом нужно исходить из имеющейся мебели кухонного гарнитура или столовой группы.</p><p>Люстра для кухни в современном стиле должна отличаться креативным дизайном, созданным с использованием самых современных инновационных материалов. Сюда можно отнести функциональные светильники в лаконичной стилистике шарообразной, кубической, цилиндрической формы или же эффектные образцы светильников от именитых дизайнеров.</p><p><img src=\"/img/blog/4a0b3fcc6265dfc63919c36401c15318.jpg\" alt=\"Люстры для кухни\" title=\"Люстра для кухни\" /></p><p>В кухне классического дизайна найдут свое место люстры-канделябры традиционной формы, украшенные хрустальными или стеклянными подвесками. Здесь нередко встречаются классические люстры с тканевыми плафонами. Отметим, что такие светильники просто созданы для обеденной зоны и кухни-гостиной, но в небольшой кухоньке или недалеко от зоны приготовления пищи такая роскошная люстра будет быстро загрязняться.</p><p>Если ваша кухня выдержана в стиле кантри, прованс или итальяно, то ее стоит украсить люстрой с элементами ковки с дампами-свечками. Гармонично на кантри-кухне будет выглядеть люстры с искусственно состаренными элементами отделки, напоминающими потускневшую от времени бронзу или серебро.</p><p><img src=\"/img/blog/7f2810bcdd96f247e4bb0f99818770ef.jpg\" alt=\"Люстры для кухни\" title=\"Люстра для кухни\" /></p><p>Любители этнических направлений, например, японского минимализма, могут оснастить свою кухню этно-светильниками стилизованными японскими люстрами, изготовленными из дерева с абажуром из рисовой бумаги, марокканскими фонарями, моделями из разноцветного стекла и т. п.</p><p>Ну, а тем, кто не хочет становиться приверженцем определенного стиля, эксперты рекомендуют присмотреться к современным тенденциям и проявлять больше фантазии при оформлении интерьеров своего дома. Ведь нет никаких ограничений, поэтому люстры для кухни не обязательно должны идеально соответствовать общей стилистике помещения.</p><p>Сейчас широко востребованы эклектичный интерьеры и стиль фьюжн, представляющий собой смесь разностилевых компонентов. Здесь даже люстра из хрусталя находит своё необычное место на кухне хай-тек или в интерьере, выдержанном в духе минимализма.</p><p><img src=\"/img/blog/b44749d5fa339af03cd5f0d7357515c6.jpg\" alt=\"Люстры для кухни\" title=\"Люстра для кухни\" /></p><p>Ставка на контраст исключительно смелый и впечатляющий способ создать на своей кухне эффектный и выразительный интерьер, привлекающий внимание своей новизной и необычностью. Габаритные размеры светильников обусловлены высотой потолков и площадью кухонных комнат. Чем меньше размеры этих показателей, тем люстра должна быть компактнее. На малогабаритных кухнях оптимально будут смотреться светильники с небольшими плафонами, прижатыми к потолку, или люстры накладного типа в форме тарелок. На языке специалистов эти люстры называют припотолочные.</p><p>Когда люстре отводится функция основного источника освещения, то ее стоит прикрепить в центральной части потолка. Это будет оптимально в маленькой кухне, но в кухнях просторного типа с отдельной столовой смысла в таких люстрах нет, так как там центральная часть помещения кухни, как правило, пустует. В этом случае нужно разместить люстру в центре столовой зоны, а над рабочими поверхностями понадобятся дополнительные источники света.</p><p>Неплохим вариантом будет разместить люстру над центральной частью стола. Но нужно учесть, чтобы поток света не получался чересчур ярким, а сама столовая зона должна быть хорошо освещена.</p><p>Немаловажным фактором при выборе люстры будет безопасность. На кухне лучше всего использовать светотехнические приборы, оснащенные усиленной защитой от загрязнения и брызг. Текстильные и бумажные абажуры для кухни не самый выгодный вариант, так как они быстро загрязняются, да и лампочки в таких люстрах обычно маломощные. Если же сменить лампу на более мощный вариант, то проявится угроза пожара. Поэтому лучше всего использовать на кухнях люстры из немарких и прочных материалов стекла, пластика, металла.</p><p>Современные высокотехнологичные светотехнические изделия могут порадовать пользователей наличием многочисленных удобных функций, облегчающих использование. Например, очень комфортно будет использование особого выключателя, оснащенного опцией коррекции мощности потока света. С его помощью можно отрегулировать параметры яркости света, исходя из внешнего освещения, определенной ситуации или ваших желаний.</p><p>Для обладателей просторных квартир с кухнями-гостиными особо удобной будет люстра с пультом дистанционного управления. Обычно пульт ДУ входит в комплект современных светодиодных люстр. Благодаря этой функции, можно не вставая с места контролировать освещение в доме, включая или выключая, или же корректируя яркость освещения.</p><p>В каталоге нашего интернет-магазина Sevenlight.ru вы сможете найти широкий ассортимент светильников для кухни, выполненных в самых разных стилях с различными техническими решениями. Мы гарантируем непревзойдённое качество от мировых производителей и самые низкие цены на продукцию. А если вы затрудняетесь при выборе люстры для кухни, то вам помогут профессиональные консультанты и менеджеры.</p><p>Мы несем свет в каждый дом!</p>', 1),
(14, 'Люстры в стиле прованс, классические люстры. Все о стилях люстр', 'stili-lyustr', 'ebb7cf8f1532b79156379fad092c45ba.jpg', 24, 'Основные стили люстр. Люстры прованс, классические люстры, современные люстры для кухни', 'Люстры в стиле прованс, классические люстры. Все о стилях люстр', 'Данная статья ознакомит Вас с самыми востребованными стилями люстр. Это люстры в стиле прованс, люстры в стиле лофт. Классические люстры и люстры в стилях хай-тек и модерн.', '<p>Данная статья будет особенно интересна тем, кто находится в состоянии поиска наиболее подходящей люстры для определенного интерьера либо собирается обновить дизайн помещения, оформив все &ndash; интерьер, мебель и освещение &ndash; в едином стиле. Именно с такой целью в нашем интернет-магазине можно воспользоваться специальным разделом <a href=\"/catalog/lyustry-po-stilyu#h1\" target=\"_blank\">Люстры по стилю</a> и ознакомиться со всеми люстрами в соответствии со стилем, в котором они выполнены. И чтобы легче было определиться с выбором люстры того или иного стиля, мы предлагаем краткое обзорное описание шести основных стилистических направлений.</p>', '<p>h1&gt;Стили люстр</p><p>Все, кто хочет приобрести для своего офиса или дома стильные, качественные и недорогие светильники или люстры, без труда смогут найти в интернете наши магазин Sevenlight.ru. Для того, чтобы удовлетворить запросы самых требовательных покупателей мы предлагаем на страницах своего интернет-портала несколько тысяч моделей самых различных светоосветительных приборов. Данная статья будет особенно интересна тем, кто находится в состоянии поиска наиболее подходящей люстры для определенного интерьера либо собирается обновить дизайн помещения, оформив все интерьер, мебель и освещение в едином стиле. Именно с такой целью в нашем интернет-магазине можно воспользоваться специальным разделом <a href=\"/catalog/lyustry-po-stilyu#h1\" target=\"_blank\">Люстры по стилю</a> и ознакомиться со всеми люстрами в соответствии со стилем, в котором они выполнены. И чтобы легче было определиться с выбором люстры того или иного стиля, мы предлагаем краткое обзорное описание шести основных стилистических направлений.</p><h1><a href=\"/catalog/lyustry-provans\">Люстры в стиле прованс</a></h1><p>Французский вариант стиля кантри называют провансом. В этом стиле воплотился дух Франции. В провансе акцент сделан на деревенскую мебель простой формы в светлых тонах. Обилие ярких солнечный оттенков, напоминающих выцветшие со временем картины, формируют комфортную домашнюю атмосферу. В стиле прованс все стремится к простоте, минимуме мебели и лаконичности. Для горожан, которые устали от напряженной городской жизни, прованс словно глоток свежего воздуха, позволяющий ощутить неспешный упорядоченный уклад французской деревушки. Стиль прованс отличается от других направлений требовательностью и индивидуализмом. Все детали интерьера должны подчиняться общему зачину, создавая особое настроение. <br /> <br /> <img src=\"/img/blog/ebb7cf8f1532b79156379fad092c45ba.jpg\" alt=\"Люстра прованс\" title=\"Люстра в стиле прованс\" /></p><p>Разнообразные светильники и люстры в стиле прованс заслужили особое внимание. Искусственно состаренные предметы интерьера и светотехники образуют причудливую смесь, наполняя помещение французской изысканностью. С помощью разных трещинок, царапин, потертостей и выбоин подчеркивается особый статус всех предметов. Светотехнические приборы здесь играют и функциональную и декоративную роль, создавая неповторимое ощущение пасторальной атмосферы. Отличительная черта прованса обилие света, в которых вся окружающая обстановка просто должна утопать. Люстры прованс и другие светильники как нельзя лучше помогут создать целостную картину для интерьера, выполненного в этом стиле.</p><h1><a href=\"/catalog/loft-lyustry\">Люстры в стиле лофт</a></h1><p>Занимаясь оформлением своего дома, люди исходят из индивидуальных предпочтений. Творческий и жаждущий экспериментов человек нередко делает выбор в пользу стиля лофт. Этот стиль был рожден в эпоху, когда американцам пришлось превращать в жилые помещения заброшенные здания заводов и фабрик. Для создания уюта в окружении холодных стен индустриальной постройки нужно было применить эффектные компоненты и оригинальные дизайнерские решения, чтобы оптимально скрасить маловыразительную и безжизненную атмосферу заводских блоков. Спустя некоторое время подобные оформительские решения стали проникать и в обычные квартиры или частные дома. Стилистические находки приглянулись молодому поколению, творческим натурам и всем тем, кто ценит в жизни все неординарное, включая и интерьер своего жилища. Просторные индустриальные помещения испытывали дефицит естественного освещения, поэтому искусственным источникам света отводилась важная роль.</p><p>Люстры в стиле лофт и другие светотехнические приборы представляют собой образцы оригинальных осветительных приборов промышленного направления, напоминающие антиквариат или инструмент, словно пришедшие из производственных цехов середины 20 века. Здесь можно увидеть характерную лампу Эдисона из латуни, множество пучков проводов, на которых подвешены лампочки. Конечно, с учетом этих многочисленных нюансов, эти светильники нуждаются в особом отношении. Это направление стало идеальным воплощением пространства для креативных людей, не любящих ограничения. Причем следует учесть, что разнообразные светильники в сочетании с четко продуманным интерьером создают гармоничную атмосферу.</p><p><img src=\"/img/blog/b3346696bddaf42fbf443d33a9049d61.jpg\" alt=\"Люстра лофт\" title=\"Люстра в стиле лофт\" /></p><h1><a href=\"/catalog/klassicheskie-lyustry\">Люстры в классическом стиле</a></h1><p>Классика в любом ее проявлении относится к тем вещам, которые недаром называют вечными и нестареющими. В дизайне современных квартир и домов классический стиль занимает весомое место несмотря на обилие многочисленных решений, возникших за последние годы. Производители светотехнических приборов не могли не акцентировать внимание на этом популярном и изысканном стиле. Особенный интерес представляют люстры в классическом стиле, с помощью которых можно украсить любое помещение и полностью изменить восприятие старого интерьера. <br /> <br /> <img http:=\"\" 51=\"\" 15=\"\" 94=\"\" 65=\"\" img=\"\" blog=\"\" 4d81bc33eaa2baf6db2f51e4543abab6=\"\" jpg=\"\" alt=\"Люстра классическая\" title=\"Люстра в классическом стиле\" /></p><p>Как правило, здесь используют дерево, металлы, стеклянные или хрустальные элементы. Здесь нет места пластику и прочим дешевым деталям. Классика обязана быть роскошно дорогой. Порой при производстве абажуров и другого декора используют элитные ткани, изготовленные ограниченными партиями. Для украшения главных элементов применяется бронзовые, позолоченные, серебряные или латунные элементы. Большая часть компонентов этих люстр производится в ручном режиме, что в комплексе с дорогими материалами влияет на формирование недешевых расценок. Высокие цены будут характерным фактором, указывающим на качественную люстру, хотя зачастую можно встретить и более дешевые вариации. Главным отличием светильников классика можно назвать присутствие большого числа рожков или плафонов. Эти элементы обычно выполнены в однотонной расцветке, а если на них и есть рисунок, то он также одноцветный. В большинстве случаев изображения на плафонах делают из золотых или серебряных линий. Это великолепие органично дополняют подвески или прочие декоративные элементы, особенно, если речь идет об изысканных хрустальных люстрах.</p><h1><a href=\"/catalog/sovremennye-lyustry\">Люстра для кухни в современном стиле</a></h1><p>Такие светильники отличаются экстравагантностью и креативностью. В минималистично оформленном интерьере люстра станет той самой изюминкой, которая будет выделяться на общем фоне и подчеркивать его своеобразие. Разнообразные формы, материалы и оттенки помогают реализовывать идею разрешается все, что по душе. Такие люстры отличаются широким спектром внешнего дизайна: от простых и сдержанных до шикарных и футуристичных. Каждый из этих вариантов будет заключительным аккордом в создании любой обстановки с оптимальным сочетанием функциональности и красоты. Люстры в современном стиле позволят привнести роскошь эпохи рококо в обыденную реальность, сохраняя традиционные изыски и шик, но при этом идеально смогут вписаться в самую модную обстановку. <br /> <br /> <img src=\"/img/blog/f0aa07b6b34447573ab07dbd4cdf0fdb.jpg\" alt=\"Люстра современная для кухни\" title=\"Люстра в современном стиле для кухни\" /></p><h1><a href=\"/catalog/lyustry-haj-tek\">Люстры в стиле хай тек</a></h1><p>В стиле хай-тек основной акцент делается на ультрасовременные образы где преимущество отдаётся функциональным деталям, рационализму, четкости и стремительности линий, отсутствию в комнатах излишеств и пафосных декоративных элементов. Основными чертами стиля стали металлы с холодным блеском, сияющее стекло и хром, прозрачные пластиковые элементы. Хай-тек служит в дизайне воплощением неумолимого прогресса. Простота геометрической линии, ощущения перспективы в пространстве, окруженном полированными и зеркальными поверхностями, необычные преломления и отражения лучей достигаются не только за счет современных моделей мебели. Главная роль тут отводится освещению, применению в интерьере световых эффектов всевозможного характера.</p><p><img http:=\"\" 51=\"\" 15=\"\" 94=\"\" 65=\"\" img=\"\" blog=\"\" 2506cc3ad4828c74e3a64032fb929f5c=\"\" jpg=\"\" alt=\"Люстра хай тек\" title=\"Люстра потолочная хай тек\" /></p><p>Сейчас люстры в стиле хай-тек изготавливаются из инновационных материалов, облекая их в форму с обилием футуристичных элементов в конструкции. Это может быть представлено обилием разноуровневых светотехнических приборов, встроенного типа, каскадами небольших галогеновых ламп, металлических конструкций неправильной формы, с обилием угловатых линий. Основные почитатели стиля хай тек деловые и энергичные жители больших городов.</p><h1><a href=\"/catalog/lyustry-modern\">Люстры в стиле модерн</a></h1><p>Модерн как стиль в интерьере ведет свою историю с начала прошлого века. Следуя веяниям моды, вскоре появились и первые образцы люстры в стиле модерн. Отличительными признаками таких светильников являются плавные линии и ассиметричные формы. Здесь нет места лишним деталям, а то, что присутствует гармонично сочетается, формируя общую композицию, похожую на настоящее произведение искусства.</p><p><img src=\"/img/blog/4493d8658cfc36b459feeb9bd936b4a0.jpg\" alt=\"Люстра модерн\" title=\"Люстра потолочная модерн\" /></p><p>Люстры в стиле модерн сложно перепутать с другими типами. Ее отличают необычность конструкции, плавность изогнутых линий, острота или напротив лаконичность в форме плафонов. При виде такое люстры поневоле поверишь, что фантазия дизайнеров не имеет границ. Сложность форм, изобилие множества деталей или наоборот, строгие и лаконичные форматы стиля модерн всегда привлекают внимание окружающих. Эти люстры можно представить в виде некоего сочетания торжественности, пышности классики и неординарного хай-тека. Изобилие лампочек в люстрах, обрамление из простых плафонов шарообразной формы, цветов и прочих необычных форматов не только служат для освещения комнаты, но и дают возможность акцентировать внимание на прочих элементах в интерьере.</p><p>Мы уверены, что любой посетитель нашего магазина из всего многообразия товаров, представленных на портале Sevenlight.ru, сможет выбрать люстру, которая идеальным образом впишется в интерьер квартиры или загородного коттеджа. Безусловно, что все покупатели могут рассчитывать на любую консультацию: свяжитесь с нашим персоналом по почте или телефону и получите ответы все вопросы, касающиеся не только дизайнерского оформления, но и технических возможностей любой модели светильников.</p>', 1),
(15, 'Светодиодные потолочные люстры', 'svetodiodnye-potolochnye-lyustry', '2016d50aa49a6bfb1be536c799bee467.png', 21, 'Светодиодные потолочные люстры', 'Светодиодные потолочные люстры', 'Ликбез о светодиодных потолочных люстрах', '<p>Благодаря современным технологиям мы получили множество уникальных изобретений, в число которых входят светодиодные источники света. Они с успехом заменили традиционные лампы накаливания во многих светотехнических приборах, а люстры светодиодные потолочные стали настоящим прорывом в дизайне домашнего интерьера. С их помощью можно эффектно подчеркнуть особенности внешнего вида жилого помещения. Эти потолочные люстры могут выполняться в различных популярных направлениях, начиная от нестареющей классики и заканчивая модерном и арт-деко. С легкой руки талантливых дизайнеров светодиодные люстры стали украшать компонентами из стекла и даже из натурального камня. Их ключевой особенностью является минимальное выделение тепла, что намного улучшает атмосферу в помещении.</p>', '<h1><a href=\"/catalog/svetodiodnye-potolochnye-lyustry#h1\">Люстры LED</a></h1><p>Благодаря современным технологиям мы получили множество уникальных изобретений, в число которых входят светодиодные источники света. Они с успехом заменили традиционные лампы накаливания во многих светотехнических приборах, а люстры светодиодные потолочные стали настоящим прорывом в дизайне домашнего интерьера. С их помощью можно эффектно подчеркнуть особенности внешнего вида жилого помещения. Эти потолочные люстры могут выполняться в различных популярных направлениях, начиная от нестареющей классики и заканчивая модерном и арт-деко. С легкой руки талантливых дизайнеров светодиодные люстры стали украшать компонентами из стекла и даже из натурального камня. Их ключевой особенностью является минимальное выделение тепла, что намного улучшает атмосферу в помещении.<br /><br /><img src=\"/files/uploads/artelamp_a9228pl6wh.1024x768w.png\" alt=\"\" width=\"522\" height=\"522\" /></p><ul><li><h4>Преимущества</h4></li></ul><p>Достоинства, которыми обладают светодиодные люстры потолочные для дома очевидны: они обрели невиданную популярность среди потребителей за счет экологически безопасной работы, устойчивостью к вибрациям и прочностью, которая и не снилась обычным лампочкам. Они обладают повышенным сроком службы, потребляют мало электроэнергии и могут функционировать в широком спектре температур. Производители гарантируют пятилетний срок стабильной работы светодиодных элементов, но на деле они могут прослужить гораздо дольше. К значимым преимуществам этой светотехнической продукции отнесем и высококачественный уровень подсветки, что позволяет комфортно чувствовать себя в помещении, выполнять свою работу с повышенной энергией, а, кроме того, доказано &ndash; при нахождении в комнате, освещаемой светодиодными светильниками, улучшается настроение и самочувствие.</p><p><img src=\"/files/uploads/artelamp_a9354pl5wh.1024x768w.png\" alt=\"\" width=\"478\" height=\"478\" /></p><p>&nbsp;</p><ul><li><h4>Особенности</h4></li></ul><p>Светодиодная осветительная продукция отличается широким спектром использования, их можно монтировать на обычные потолки, гипсокартонные или натяжные. Сейчас промышленность выпускает потолочные люстры любой формы и размера, расцветки, текстуры, а также их дополнительно оснащают удобными техническими опциями и пультом дистанционного управления. У светодиодных светильников отсутствуют лампы как таковые и патроны, а их размеры в разы меньше традиционных. Светильники практически не нагреваются, включаются в одно мгновение, не мигают и не искажают цветовосприятие. Светодиоды обладают широким спектром оттенков, начиная от холодного голубого свечения и заканчивая теплым желтым светом. По мнению специалистов, в будущем led люстры и другие источники света на светодиодах вытеснят классические образцы, так как они более удобны, практичнее и экономичнее лампочек накала.</p><p>Светодиодные приборы обычно питаются от электросети с пониженным напряжением, что требует в процессе подключения использовать дополнительный блок питания. Светодиодные люстры потолочные для дома такими блоками уже оснащены или же в нем нет необходимости. Свечение от светодиодных источников максимально приближено в плане цветового оттенка к естественному дневному. Многие пользователи отдают предпочтение более &laquo;теплым&raquo; оттенкам, что нужно учитывать при покупке осветительных приборов на светодиодах, поскольку оттенки свечения могут быть самими разными. Цена светодиодных люстр выше, чем у традиционных аналогов. Это обусловлено более дорогими производственными затратами на выпуск светодиодов и техническими нюансами, которые влияют на конечную стоимость. В магазинах можно найти и варианты подешевле, но в таких недорогих светильниках вы не найдете дополнительных возможностей и функций.</p><p>Многие светодиодные люстры оснащаются удобной опцией &ndash; пультом дистанционного управления, с помощью которого можно управлять освещением, не покидая любимое кресло или диван. Наличие такой функции делает люстру дороже, поэтому если вы не хотите переплачивать, то выбирайте модели без пульта ДУ. Вторая приятная опция &ndash; возможность изменять яркость свечения или менять цвет светового потока. Технические свойства диодов позволяют выполнить и такие задачи, но за этот дополнительный комфорт придется заплатить больше, чем за модели без расширенных опций. Сейчас встречаются и модели, в которых предусмотрено использование светодиодов наряду с лампами накаливания. Подобные светильники подойдут тем, кому не нужен постоянный яркий свет. Лампу накала можно включать для освещения общего фона, когда не нужно яркое освещение, а светодиодная часть будет незаменима, чтобы освещать помещение поинтенсивнее.</p><ul><li><h4>Светодиодная люстра своими руками</h4></li></ul><p>С учетом распространённости компонентов, многие народные умельцы пробуют самостоятельно собирать светодиодные светильники. Конечно, это не суперсложная операция, так как для этого необходимо знать базовые понятия электрики и приобрести нужные детали. Но есть убедительные аргументы против кустарного производства. Домашние условия не позволят получить такую точность и качество обработки материалов, поэтому самодельные светильники по своему внешнему виду будет сильно уступать фабричным изделиям. Вторым важным фактором будет вопрос безопасности. Производители тратят огромные деньги, время и ресурсы для того, чтобы их продукция отвечала всем требованиям безопасности. Доморощенные кулибины вряд ли будут обращать достаточное внимание на этот пункт. А при сборке устройства, порой, даже качество и длина проводов имеют решающее значение. Понятно, что в самодельной светодиодной люстре будет невозможно установить и дополнительные блоки наподобие дистанционного управления, а при их отдельной покупке цена изделия может превысить модель люстры из магазина.</p><ul><li><h4>Нюансы выбора светодиодной люстры</h4></li></ul><p>При покупке люстры обязательно учитывайте некоторые важные факторы, от которых в дальнейшем будет напрямую зависеть ее использование. Большие светодиодные люстры будут уместны не в любых комнатах. Здесь важно подобрать оптимальные размеры светильников. Не думайте, что люстра меньшего размера будет хуже справляться со своими обязанностями. Это как раз тот случай, когда размер не имеет значения. В досветодиодную эпоху сила освещения была зависима от числа лампочек в люстрах. Сейчас нужно больше акцентировать внимание на эстетическую составляющую, определить, как впишется та или иная люстра в общий дизайн помещения, ведь мощность современного светодиода в разы больше стандартных лампочек.<br /><br /><img src=\"/files/uploads/favourite_167810u.1024x768w.png\" alt=\"\" width=\"550\" height=\"300\" /></p><p>В нашем интернет-магазине Sevenlight.ru вы найдете огромный ассортимент качественных светодиодных люстр от мировых производителей. У нас представлены <a href=\"/catalog/svetodiodnye-potolochnye-lyustry#h1\" target=\"_blank\">люстры светодиодные потолочные</a> на любой вкус, для любого типа интерьеров и для любого кошелька. Если вы хотите, чтобы атмосфера вашего дома засверкала новыми красками, то с помощью современной светотехнической продукции, представленной в онлайн-каталоге это будет сделать легче легкого!</p>', 1),
(16, 'Люстры Евросвет', 'lyustry-evrosvet', '9274912165c68603a14651a12cf6039c.png', 17, 'Люстры Евросвет - доступная роскошь', 'Люстры Евросвет', 'Люстры и светильники от передовой фабрики Евросвет', '<p>Многие мировые производители выпускают качественную и оригинальную продукцию, завоевывающую внимание потребителей. В их числе представлено немало отечественных компаний, среди которых лидирующие позиции на рынке занимает крупнейший российский производитель, выпускающий светотехническое оборудование под брендом &laquo;Евросвет&raquo;.</p>', '<h1>Люстры Евросвет в каждый дом</h1><p><img src=\"/img/blog/9274912165c68603a14651a12cf6039c.png\" alt=\"Люстры Eurosvet\" title=\"Люстры Eurosvet\" /></p><h2><a href=\"/catalog/lyustry-eurosvet\">Все люстры Евросвет</a></h2><p>Создавая уникальный и комфортный облик своего дома, важно уделить внимание одному из самых значимых элементов любого помещения освещению. Правильно подобранные осветительные приборы не только выполнят свою основную функцию, но и станут существенной частью интерьера комнаты. Многие мировые производители выпускают качественную и оригинальную продукцию, завоевывающую внимание потребителей. В их числе представлено немало отечественных компаний, среди которых лидирующие позиции на рынке занимает крупнейший российский производитель, выпускающий светотехническое оборудование под брендом Евросвет.</p><p>Используя светильники и люстры Евросвет, можно эффектно акцентировать внимание на отдельных компонентах, формирующих облик дома, при этом их светотехнические приборы могу стать изюминкой в интерьере комнаты. Сейчас промышленность предлагает огромный ассортимент светильников разных видов, формы и направлений в стиле, начиная от нестареющей классики и заканчивая ультрасовременными тенденциями в стиле хай-тех и модерн. Компания Евросвет рада предложить большой выбор как классических, так и элитных кованных образцов люстр, светильников для детских комнат, хрустальных люстр и многое другое осветительное оборудование для оснащения помещений любого типа.</p><p>У нас вы найдете светильники подвесного и потолочного типа, люстры с пультом дистанционного управления и с подсветкой, причем это далеко не полный список. Мы поможем сделать ярче и светлее любые пространства от маленьких комнатушек до банкетных залов.</p><p>Евросвет в России знают давно, компания занимается продажами уже 18 лет, а география поставок охватывает не только всю территорию нашей необъятной Родины от Калининграда до Камчатки, но и простирается далеко за ее пределы. В линейке продукции компании представлены более полутора тысяч моделей, которые отвечают всем современным трендам в дизайне интерьеров. Евросвет стал символом качественных светильников, в которых гарантируется отменная работа каждой детали. В миллионах российских семей домашний уют помогают создавать светильники и люстры Евросвет.</p><p>Компания Евросвет отмечает в этом году 19 лет. Почти два десятилетия успешной и стабильной работы позволили этому российскому бренду занять достойное место в ряду отечественных и зарубежных конкурентов. Все это стало возможным благодаря слаженной работе команде специалистов, настоящих профессионалов своего дела. Наличие собственного дизайнерского бюро с опытными и талантливыми сотрудниками позволяет компании не просто идти в ногу с мировыми тенденциями моды в сфере осветительного оборудования, но и создавать свою эксклюзивную продукцию, способную произвести впечатление на самого взыскательного покупателя.Кроме первоклассных российских специалистов, компания сотрудничает с известными европейскими художниками, которые вносят свой неоценимый вклад в развитие бренда и разработку эффектных дизайнерских решений.</p><p>Для организации эффективной логистики и доставки в компании работает собственный складской комплекс, в котором постоянно поддерживается не менее 90% от ассортимента продукции. Это позволяет удовлетворять постоянный растущий спрос на светотехнические изделия и в короткие сроки осуществлять отгрузки и доставку заказов по всей России. Наша компания Sevenlight.ru успешно сотрудничает с партнерами в области оптовых поставок, для которых мы можем предложить уникальные взаимовыгодные условия работы.</p><p><img src=\"/img/blog/023989725dd15e729f1709f310622cee.png\" alt=\"Светильники и люстры Евросвет\" title=\"Люстры Eurosvet\" /></p><p>Благодаря тому, что ключевые производственные мощности расположены на территории Китая, компания Евросвет может проводить доступную и гибкую ценовую политику, что не отражается на качестве и надежности продукции. Все выпускаемые изделия проходят многоуровневый жёсткий контроль, начиная с комплектующих, а весь процесс сборки проходит под бдительным присмотром ОТК, что исключает появление некачественных изделий. Светотехнические изделия производятся на высокотехнологичном оборудовании с использованием самых современных материалов и качественных комплектующих, которые прошли все предварительные испытания и по своим показателям соответствуют всем техническим нормативам и стандартам качества. Выросшая популярность продукции от этого производителя красноречиво говорит сама за себя. Миллионы довольных покупателей, проголосовавших рублем за светотехнические изделия от Евросвета, стали самой эффективной и справедливой гарантией качества, подтвердив отменную репутацию производителя.</p><p><img src=\"/img/blog/e06099d0a49fc65fe71cd045f8236c96.png\" alt=\"Светильники и люстры Евросвет\" title=\"Люстры Eurosvet\" /></p><p>Внушительный спектр продукции компании можно условно подразделить на несколько торговых групп, каждая из которых обладает своей отличительной чертой. Евросвет люстры каталог включает в себя следующие категории продукции:</p><ul><li>Галоген здесь представлена линейка классических люстр, созданных с применением элементов из хрусталя высшего качества Strotskis. Здесь вы сможете выбрать как небольшие стандартные люстры, так и шикарные многоуровневые изделия.</li><li>Мегаполис образцы классических плафонов, оснащенных светодиодными элементами и арматурой в стиле хай-тек, и светодиодные люстры Eurosvet. Благодаря светодиодам в этих светильниках появилась возможность управлять световым потоком с помощью пульта дистанционного управления, контролируя мощность освещения.</li><li>Модерн бра настенного типа и светильники в виде потолочных тарелок в форме квадратов, кругов.</li><li>Классические офисные настольные лампы.</li><li>Оптима самая бюджетная серия светильников, входящая в число лидеров продаж, с оптимальным соотношением цены и качества, позволяющая недорого и эффектно оформлять интерьеры помещений разного назначения, начиная от квартир и заканчивая торгово-промышленными объектами.</li><li>Подвеcные светильники идеальный вариант для акцентного освещения, зонирования пространства в квартире для создания направленных потоков света над столом, барной стойкой, трюмо и т.п.</li><li>Споты светильники в современных дизайнерских решениях, оснащенные поворотными функциями, дающие возможность управлять потоком света, направляя его в нужное место. Благодаря поворотному механизму можно контролировать угол распространения светового потока, чтобы достичь оптимально комфортного уровня освещения для того или иного случая.</li><li>Хрусталь классические образцы шикарных люстр, где в качестве основного материала использован высококачественный хрусталь Strotskis (от фамилии гендиректора компании Евросвет). Данный материал компания производит сама, что делает цены на люстры доступными, а показатель наличия оксида свинца достигает 24%, что свидетельствует о высококачественном хрустале высшей пробы.</li><li>Элегант одно из ответвлений классического стиля, которое отличается более сдержанным исполнением, подходящим для помещений с невысокими потолками.</li></ul><p><img src=\"/img/blog/bbb66dbc6d695d8033c25eab3fc0ba1d.png\" alt=\"Светильники и люстры Евросвет\" title=\"Люстры Eurosvet\" /></p><p>Кроме этого в интернет-магазине компании Евросвет в каталог товаров включена и эксклюзивная продукция польского бренда Bogates. Польская промышленность давно славится высококачественными техническими изделиями, а в этом случае мастерство исполнения и художественного воплощения стали настоящим триумфом в мире интерьерного дизайна и технического наполнения. Эта марка известна своей богатой историей и утонченным дизайном продукции. Коллекция изысканных светильников высшего класса станет настоящей изюминкой интерьера в любом помещении. Все люстры от Bogates создаются по индивидуальным эскизам ведущих европейских дизайнеров, которые вкладывают в свою работу свежие идеи и инновации в области освещения. Евросвет интернет магазин предлагает продукцию элитного бренда для людей, ценящих достойное качество жизни и непревзойденный уровень комфорта. Bogates станет настоящим откровением в мире светового оборудования. Качественный свет и изысканное воплощение создадут волшебную атмосферу, наполняя интерьер роскошным сиянием.</p><p>Познакомьтесь с новым видом на классические формы. Рецепт шикарной жизни от Bogates: изящество силуэтов, сияние хрусталя высшей пробы и инновационные находки европейских дизайнеров. На сайте евросвет люстры от Bogates олицетворяют роскошь, которую ценили всегда и будут ценить еще долгое время. Под этим брендом компания предлагает изысканные люстры от романского стиля до арт-деко, от классических люстр с подвесками из хрусталя до спартанского лофта, которые непременно займут центральное место в композиции вашего интерьера. С помощью этих шикарных люстр можно успешно и недвусмысленно подчеркнуть высокий статус апартаментов обладателя.</p><p>Компания Евросвет успешно на протяжении многих лет является эксклюзивным дилером продукции польского бренда TK LIGHTING, которая характеризуется европейским качеством, использованием экологически чистыми материалами, представленными в современном дизайне. Арматурная часть светильников создается из массива натуральной древесины без использования металлического крепежа и клея, а на плафоны идет материал из натурального льна или сатина, обработанного тефлоновой пропиткой, которая не нагревается и не воспламеняется.</p><p>В нашем интернет-магазине Sevenlight.ru вы сможете найти <a href=\"/catalog/lyustry-eurosvet\" target=\"_blank\">всю гамму продукции от компании Евросвет</a>. У нас даже самый взыскательный покупатель не останется без покупки. Светильники и люстры Евросвет помогут вам создать в своем доме комфортную атмосферу, наполненную уютным светом, созданным бережно и с любовью.</p>', 1),
(17, 'Распродажа люстр и светильников', 'rasprodazha-lyustr-i-svetilnikov', '6a70e3f6dfa7f7c6fc0e6155de22cdb7.png', 15, 'Распродажа люстр и светильников', 'Распродажа люстр и светильников', 'Распродажа люстр и светильников. Актуальные акции от Sevenlight.ru', '<p>Красивые, со вкусом подобранные источники света всегда будут находиться в тренде, поэтому сезонные и прочие распродажи &ndash; чрезвычайно удобный способ купить лучшую вещь по очень доступной цене. href=\"https://sevenlight.ru/catalog/rasprodazha\" target=\"_blank\"&gt; Распродажа люстр в интернет-магазине .позволяет нашим клиентам заказать модельное изделие, отличающееся нетривиальной конфигурацией. Наш каталог предоставляет возможность выбора любой модели, которая станет настоящей изюминкой детской, спальни, гостиной, столовой или ванной комнаты.</p>', '<h1>Распродажа люстр самое время обновить интерьер</h1><p>Красивые, со вкусом подобранные источники света всегда будут находиться в тренде, поэтому сезонные и прочие распродажи чрезвычайно удобный способ купить лучшую вещь по очень доступной цене. <a href=\"/catalog/rasprodazha\">Распродажа люстр в интернет-магазине</a> позволяет нашим клиентам заказать модельное изделие, отличающееся нетривиальной конфигурацией. Наш каталог предоставляет возможность выбора любой модели, которая станет настоящей изюминкой детской, спальни, гостиной, столовой или ванной комнаты.</p><p>Стоит отметить, что в нашем магазине никогда не бывает фиктивных распродаж когда цены предварительно специально завышаются. Работая с официальными производителями, мы имеем возможность значительно снижать стоимость товаров в два и более раза. К тому же регламентируемая МРЦ ценовая политика предусматривает наложение штрафов в случае ее нарушения. Мы очень дорожим своей репутацией, поэтому на страницах виртуального каталога наши покупатели никогда не увидят некачественных или бракованных изделий.</p><p><img src=\"/img/blog/6a70e3f6dfa7f7c6fc0e6155de22cdb7.png\" alt=\"Распродажа люстр\" title=\"Распродажа люстр\" /></p><p>Люстры со скидкой можно также очень выгодно купить при распродажах изделий, которые уже отработали небольшой срок в шоу-румах или на витринах. Они предлагаются покупателям по очень приятным ценам, при этом их качество и общая функциональность совершенно не меняются. Также весьма выгодно для клиентов покупать модели люстр, снятых с производства, как правило, скидка на распродажные серии доходит до 50%. В таких случаях довольно часто возникает ситуация, когда нам нужно срочно распродать остатки продукции со склада.</p><p>Присутствующие сертификаты качества, выдаваемые известными торговыми марками, являются надежной гарантией того, что покупатель получит отличный по всем параметрам осветительный прибор. Более того постоянное сотрудничество с нами предохраняет покупателей от приобретения подделок. Распрожадные люстры не теряют гарантии.</p><p>Люстры со скидкой считаются великолепным вариантом покупок в тех случаях, когда нужно сделать хороший подарок близким, знакомым или сотрудникам. Также это просто идеальный способ обновить интерьер дома или квартиры молодоженам, у которых каждая копейка на учете. А тех, кто привык окружать себя стильными и добротными вещами, несомненно, порадует факт, что оригинальная, богатая по дизайну люстра обошлась ему по совершенно смешной цене.</p><p><img src=\"/img/blog/1faf28118baebff74e7a24dba258eb99.png\" alt=\"Распродажа люстр\" title=\"Люстры со скидкой\" /></p><p>Для того чтобы найти для себя что-либо новенькое по оптимальной стоимости, совсем необязательно постоянно следить за поступлениями каталога. Вам нужно только заходить периодически на страницу <a href=\"/catalog/rasprodazha\">РАСПРОДАЖА</a> . Здесь вы увидите, насколько разнообразен мир скидок и интересных предложений в нашем магазине.</p><p>Если вы еще не решили, где будете покупать осветительные приборы обязательно загляните к нам. Люстры, распродажа которых при любой ситуации является приятным сюрпризом, имеются здесь в полном ассортименте, способном удовлетворить самого капризного покупателя.</p>', 1);
INSERT INTO `s_blog` (`id`, `name`, `trans`, `image`, `image_id`, `meta_title`, `meta_keywords`, `meta_description`, `annotation`, `text`, `visible`) VALUES
(18, 'Люстры с пультом', 'lyustry-s-pultom', 'dd8df3f97a28e25da69cd9d0b364855b.png', 12, 'Люстры с пультом', 'Люстры с пультом', 'О люстрах с пультом, на что следует обратить внимание при выборе.', '<p>Чем на самом деле является оригинальный осветительный прибор, управляемый дистанционно? Светильником для ленивых или все-таки чрезвычайно удобным устройством, облегчающим повседневность для человека? Надеемся, поданная ниже информация позволит вам разобраться в этом вопросе.</p>', '<h1>Светодиодная люстра с пультом управления креатив на потолке</h1><p>В РАЗДЕЛ <a href=\"/catalog/lyustry-s-pultom\">ЛЮСТРЫ С ПУЛЬТОМ</a></p><p>Чем на самом деле является оригинальный осветительный прибор, управляемый дистанционно? Светильником для ленивых или все-таки чрезвычайно удобным устройством, облегчающим повседневность для человека? Надеемся, поданная ниже информация позволит вам разобраться в этом вопросе.</p><p>Люстры с пультом управления пользуются весьма неплохим спросом в последнее время у наших клиентов. Они не только выполняют свои основные функции по освещению пространства в помещении, но и являются чрезвычайно самобытным украшением интерьера. Кроме того  такие светильники обладают возможностями менять внутреннюю атмосферу помещения на протяжении всего дня, благодаря предусмотренным встроенным функциям.</p><p><img src=\"/img/blog/dd8df3f97a28e25da69cd9d0b364855b.png\" alt=\"Люстра с пультом\" title=\"Люстра с пультом\" /></p><h2>В чем заключаются их ключевые особенности?</h2><p>Конструкционная специфика таких приборов предусматривает обязательное наличие пульта для осуществления управления на дистанции и своеобразного приемника. Источниками питания для пульта выступают обычные батарейки.</p><p>В зависимости от типа исполнения изделия, люстры могут быть светодиодного типа либо сочетаемыми с галогенными лампочками. Как правило, диапазон действия пульта управления достигает от 10 до 100 метров.</p><p>Внешне такое изделие представляет собой обычную люстру, но на самом деле ему присущи некоторые скрытые свойства:</p><ul><li>каркас, выполненный исключительно из прочного металла;</li><li>наличие контроллера, вмонтированного в основу из металла;</li><li>рассеиватели плафонного типа, для изготовления которых используется стекло или хрусталь;</li><li>реле индукционного или электронного вида, необходимое для понижения напряжения.</li></ul><p>Отдельные потолочные люстры с пультом управления имеют диммер датчик движения, который может корректировать яркость света. Диммер содействует с лампочками любого типа, кроме люминесцентных, поскольку они просто не горят при пониженном напряжении. Помимо этого, каждая модель осветительного прибора с ДУ характеризуется некоторыми дополнительными функциями. К примеру, такие люстры можно запрограммировать на определенную продолжительность периода свечения.</p><p><img src=\"/img/blog/c8d7851b2fd29ce57fc0dc7882cbf4fd.png\" alt=\"Люстра citilux\" title=\"Люстра Citilux\" /></p><p>С помощью пульта можно регулировать яркость освещения, цветовую гамму подсветки, которая может быть красной, синей или сиреневой. Выбирая режим освещения, потребитель имеет возможность включить сразу все лампочки, или какую-то их часть. Особенный интерес у многих покупателей вызывает светодиодная люстра с пультом управления, обладающая музыкальным центром, встроенным в корпус. Заглянув на страницу <a href=\"/catalog/lyustry-s-pultom\">ЛЮСТРЫ С ПУЛЬТОМ</a>, вы убедитесь в том, насколько разнообразен ассортимент продукции, представляющей современные люстры с пультом ДУ.</p><p><img src=\"/img/blog/ddb21b519588579b8a06643278f8d12a.jpg\" alt=\"Люстра светодиодная с пультом\" title=\"Люстра светодиодная с пультом\" /></p><p>Говоря о достоинствах этих осветительных приборов, нельзя не упомянуть их бесспорное преимущество. Включать и выключать свет, осуществлять регулировку, а в некоторых случаях совмещать все это с прослушиванием музыки, можно, в буквальном смысле слова, не вставая с дивана. Возможно, именно поэтому регулируемые пультом люстры чаще всего предпочитают устанавливать в спальнях. Учитывая дальность управления, послать сигнал с пульта можно практически из любой точки жилого помещения, даже из соседней комнаты. Также использование этих люстр весьма удобно тем, что в случае выхода из строя батареек всегда можно воспользоваться стационарным выключателем.</p><h2>Особенности выбора</h2><p>Потолочная светодиодная люстра с пультом управления бесспорно, чрезвычайно эффектное решение для дизайнерского оформления разных комнат. Помимо спальных помещений, они отлично смотрятся в гостиных, залах, холлах.</p><p>Во-первых, надо обязательно учитывать мощность люстры. Другими словами, потребителю необходимо точно знать параметры комнаты, где будет находиться прибор и мощность люстры, которая будет находиться в этом помещении</p><p>Во-вторых, нужно непременно обратить внимание на габариты самого изделия, а также на его стилевую принадлежность. К примеру, хрустальная люстра премиум класса, величественная и торжественная, будет смотреться совершенно неуместно в комнате, отделанной в стиле хай-тек, или в детской комнате. Чтобы она полноценно подчеркивала богатое убранство помещения, ее следует поместить в комнате с классическим стилем оформления.</p><p>Люстры с пультом управления по-настоящему функциональны, безопасны, надёжны и долговечны. В этом вы обязательно убедитесь, если посетите наш сайт и сделаете свой выбор.</p>', 1),
(19, 'Купить люстру в интернет магазине - все, что нужно знать об этом', 'kupit-lyustru-v-internet-magazine---vse-chto-nuzhno-znat-ob-etom', '48e3bc5113969c9c4e15a5a06164467b.png', 11, 'Купить люстру в интернет магазине - все, что нужно знать об этом', 'Купить люстру в интернет магазине - все, что нужно знать об этом', 'О покупке люстр в интернет магазинах. Плюсы и удобства.', '<p>В последнее время практически все потребители оценили бесспорное преимущество приобретения товаров через интернет-магазины. Такой тип шоппинга чрезвычайно выгоден с разных точек зрения. К примеру, решая купить люстру в интернет-магазине, человек значительно упрощает себе жизнь, ведь именно таким образом он может найти очень много полезной информации.</p><p>&nbsp;</p>', '<h1>Купить люстру в интернет-магазине современное решение обустройства</h1><p>Если судить по последней статистке Яндекса, то каждый месяц в нашей стране осветительные приборы для своего дома, офиса или загородного имения покупают через Всемирную сеть около ста тысяч человек. Немалую лепту в это дело вносит и наш интернет магазин sevenlight.ru</p><p><img src=\"/img/blog/48e3bc5113969c9c4e15a5a06164467b.png\" alt=\"Купить люстру в интернет магазине\" title=\"Купить люстру в интернет магазине\" /></p><h2>Какие преимущества сотрудничества с нами?</h2><p>Менеджеры Sevenlight.ru делают все для того, чтобы каждый наш клиент не разочаровался в своем выборе. Мы подбираем товары под запросы покупателей, напрямую ведем диалог с заводом, если есть какие-то технические запросы по продукции, предоставляем сертификаты, консультируем по стилевому решению, догововариваемся об удобной доставке, ищем аналоги, если товара не оказалось на складе, организовываем монтаж люстр и светильников при помощи своей монтажной бригады. Производим обмен светильников или люстр на более подходящии модели в случае, когда приобретенная модель не подошла покупателю по каким-либо параметрам. Вообщем, оказываем полный цикл услуг, чтобы наш клиент был доволен покупкой. Заказывать люстры, бра, светильники и прочие осветительные приборы через виртуальный каталог чрезвычайно удобно, это экономит время и при этом мы стараемся предоставить исчерпывающую информацию.</p><p>Для посещения магазина совершенно не нужно тратить свой выходной на поиски нужной модели. Тем более, если вы постоянно сильно загружены на работе и желаете провести единственный выходной с пользой для себя, а не обивая пороги торговых точек или рынка. К тому же, желая привлечь к процессу выбора родственников или знакомых, совершенно не обязательно тянуть их в магазин достаточно только сбросить им ссылку на интересный товар по электронному адресу.</p><p>Довольно часто для того, чтобы увидеть именно то, что вам нужно, достаточно всего несколько минут. В связи с этим стоит отметить, что экономия времени это не единственный аргумент заметного преимущества таких приобретений. Практически все клиенты нашего магазина отмечают следующее:</p><h2>Финансовая экономия</h2><p>Значительная часть продукции, имеющейся у нас, предоставлена по ценам ниже, чем на рынках или в торговых центрах т.к. у нас нет издержек на аренду больщих торговых площадей, поэтому купить люстры в интернет-магазине можно действительно дешево.</p><p>Перечень наших товаров гораздо шире, нежели в обычных традиционных магазинах. Регулярное обновление ассортимента и поддержание новых тенденций моды позволяет формировать нам достойный виртуальный каталог. В отличие от других магазинов, самые первые новинки продукции появляются именно у нас, и лишь потом в других торговых точках.</p><p>Также приятной особенностью является то, что мы предлагаем товары уникальных брендов тех, которых нет на обычных рыночных прилавках и торговых точках, например, изделия от торговой марки Сitilux. Свою продукцию Citilux реализует исключительно через интернет сеть.</p><h2>Исключительное качество</h2><p>Мы не реализуем продукцию сомнительного происхождения, поскольку дорожим своей репутацией. Именно поэтому многие наши клиенты становятся постоянными, так как отличное качество абсолютно всей продукции находится у нас в приоритете. На каждую модель товара мы предоставляем сертификаты качества, выданные производителем.</p><p>Стоит отметить, что количество случаев возвращения откровенно некачественного товара в нашем магазине давно находятся практически на нулевой отметке. Объясняется этот факт тщательной проверкой каждой единицы товара еще на стадии пребывания изделия на складе. Об этом свидетельствует видео, просмотреть которое вы можете, пройдя по ссылке.</p><p><iframe width=\"425\" height=\"350\" src=\"http://www.youtube.com/embed/SD1tPMHeclM\"></iframe></p><p>В компетенцию наших служб входит и оперативная доставка изделий по указанному вами адресу в любое подходящее для вас время. Мы обязательно свяжемся с вами, чтобы уточнить все нюансы доставки. Наша система обслуживания предусматривает также и возможность самостоятельно забрать заказ из нашего пункта самовывоза. Оплата продукции производится любым удобным для клиента способом либо по безналичному расчету, либо непосредственно на месте, после получения заказанного товара.</p><p>Заказывая осветительные приборы у нас, вы, несомненно, убедитесь, что купить люстру в интернет-магазине это удобно, легко, быстро и чрезвычайно комфортно.</p>', 1),
(20, 'Как выбирать люстры - краткие рекомендации', 'kak-vybirat-lyustry---kratkie-rekomendatsii', 'd28e5ccb057b2b700c1666943073a9cb.jpg', 10, 'Как выбирать люстры - краткие рекомендации', 'Как выбирать люстры - краткие рекомендации', 'Подбор люстры для вашего дома - не сложно, а просто', '<p>Никто не будет оспаривать тот факт, что с помощью света можно поистине сотворить чудеса. Свет может не просто освещать помещения, но и пробуждает в нас положительные эмоции и чувства. Невероятные свойства света давно используются в дизайне, где он по праву занимает главенствующее место при оформлении интерьера. Благодаря таким образцам светотехнических изделий, как потолочные люстры можно запросто обновить всю окружающую обстановку, заставляя ее играть новыми красками.</p>', '<h1>Как выбрать люстру?</h1><p></p><p>{print_r($post[\'images\'])}</p><p>&lt;img src=\'{$image|resize:products:$image_id:35:35}\'&gt;</p><p>Никто не будет оспаривать тот факт, что с помощью света можно поистине сотворить чудеса. Свет может не просто освещать помещения, но и пробуждает в нас положительные эмоции и чувства. Невероятные свойства света давно используются в дизайне, где он по праву занимает главенствующее место при оформлении интерьера. Благодаря таким образцам светотехнических изделий, как потолочные люстры можно запросто обновить всю окружающую обстановку, заставляя ее играть новыми красками.</p><p>Сейчас на рынке осветительных товаров можно найти практически неограниченный ассортимент всевозможных люстр разного типа и стилевой принадлежности. Начиная от нестареющей классики в виде подвесных люстр с хрустальными абажурами и заканчивая моделями хай-тек с модерном. В последние годы у россиян все более популярными становятся люстры, оснащенные высокотехнологичными решениями, к примеру, светильники, управляемые через пульты ДУ, со светодиодами, из инновационных эко-компонентов. Поэтому вопрос как выбрать люстру становится весьма актуальным.</p><p>Сложно дать исчерпывающие рекомендации универсального характера. Каждое конкретное решение базируется на индивидуальном предпочтении и особенностях дизайна помещения, для которого подбирается люстра. К примеру, в комнаты с низкими потолками не подойдут крупногабаритные подвесные люстры, а для небольшого уютного помещения прекрасно подойдут потолочные люстры.</p><p>В том случае, если перед вами встал вопрос по выбору люстры, то нужно внимательно изучать их технические характеристики. Основные критерии, на которые в первую очередь следует обратить внимание, это:</p><ul><li>энергопотребление;</li><li>наличие нескольких режимов включения, опция регулирования мощностью освещения;</li><li>особенности патрона эти конструктивные элементы любого светотехнического прибора различны по диаметрам разъемов и материалу изготовления;</li><li>тип ламп. Мы привыкли к традиционным лампам накала, которые дарят нам характерный приятный и теплый свет, но их основной недостаток высокое потребление энергии, сильный нагрев и сравнительно небольшой срок работы. Более эффективными будут светодиодные лампы. Они обладают отличными показателями по цветопередаче и долгим сроком эксплуатации. Лампы на основе светодиодов демонстрируют хороший световой спектр, но их цена в несколько раз выше, хотя она полностью компенсируется их оптимальной экономичностью.</li></ul><p>При выборе осветительных приборов нужно учитывать также технические аспекты. К примеру, на 1 кв. метр площади необходимо около 20W света, что соответствует примерно 3 W диодных.</p><h2>Люстры в зал</h2><p>Зал или гостиную недаром называют лицом дома, ведь это то место, где собирается вся семья, туда приглашают друзей, там отмечают праздники. Выбирая люстру, учитывайте различные моменты. Свет в зале должен быть достаточно ярким с теплыми тонами, располагающими к душевному общению в уютной атмосфере. В шикарных апартаментах классического стиля с высоким потолком прекрасно будут смотреться массивные хрустальные люстры. Их изысканный торжественный внешний вид придаст интерьерам отпечаток величественного шарма.</p><p>В обычных квартирах идеальным вариантом будут потолочные люстры. Для гармоничного освещения вытянутой комнаты подойдет пара симметрично установленных приборов.</p><p><img style=\"max-width: 100%;\" src=\"/img/blog/639eca08be1bb118393e35846537dfa3.jpg\" alt=\"Люстра в зал\" title=\"Люстра для зала\" /></p><h2>Люстры для спальни</h2><p><img style=\"max-width: 100%;\" src=\"/img/blog/5f6bd1ac33cd50a814b628dc68f34e42.jpg\" alt=\"Люстра для спальни \" title=\"Люстра для спальни\" /></p><p>Спальня своеобразное сердце дома, поэтому в ней нужно обязательно создать атмосферу отдыха, расслабления, пробуждающую теплоту чувств. Люстра должна гармонично входить в единый интерьерный ансамбль, но при этом ей не стоит сливаться с общим дизайном. Строгий стиль, обилие хрусталя и матового стекла, полутона в корпусе или абажуре вот составляющие люстры для этой комнаты. Эксперты советуют обратить внимание на продукцию с разными режимами освещенности. Для спальной комнаты будут удобны люстры с пультом благодаря им вам не придется после чтения книги вставать с постели, чтобы погасить свет.</p><h2>Люстры для детской</h2><p><img style=\"max-width: 100%;\" src=\"/img/blog/e0654c33392ef2c032b0901602a04021.jpg\" alt=\"Люстра для детской\" title=\"Люстра в детсткую\" /></p><p>Детская комната уголок, где находится уютное беззаботное царство. Чтобы создать такую чудесную атмосферу, нужно тщательно подобрать световое обрамление. Главный критерий безопасность. Воздерживайтесь от покупки люстр с хрустальными компонентами или со стеклянными вставками, которые могут травмировать неосторожным обращением. Дизайнеры советуют использовать для оснащения детской комнаты потолочный тип освещения.</p><h2>Люстры для прихожей</h2><p><img style=\"max-width: 100%;\" src=\"/img/blog/6be9d09c70bcbcd809dd8a8481cc6036.jpg\" alt=\"Люстра в прихожую \" title=\"Люстра в прихожую\" /></p><p>Если театр начинается с вешалки, то дом берет свое начало в прихожей. Здесь важно создать такое освещение, которое будет формировать доброжелательную атмосферу.</p><p>Обычно прихожие не отличаются впечатляющими площадями, но это можно исправить удачно подобранным освещением. При наличии узких и длинных помещений подойдут продолговатые или прямоугольные люстры. Чтобы визуально корректировать пространство можно воспользоваться моделью с функцией корректировки световых потоков, оснащенные поворотными элементами, которые могут выделить отдельные точки.</p><h2>Люстры в кухню</h2><p><img style=\"max-width: 100%;\" src=\"/img/blog/639eca08be1bb118393e35846537dfa3.jpg\" alt=\"Люстра для кухни\" title=\"Люстра в кухню\" /></p><p>В любом доме важное место отводится кухне. Ведь именно на кухнях, за чашкой кофе обычно обсуждаются все семейные дела и происходит создание шедевров кулинарии. Освещение здесь играет важную роль. Но подходить к выбору осветительных приборов нужно с учетом особенностей кухонной зоны.</p><p>На кухне всегда повышенная влажность, а в воздухе могут парить капли жира и прочих органических элементов. Поэтому не стоит брать люстры с абажурами из текстиля, а выбрать люстры с металлическими, стеклянными или акриловыми деталями. Они не требуют особого ухода, их просто очистить от загрязнений.</p><p>Если вы решили обновить интерьер своего дома за счет смены осветительных приборов, то в нашем интернет-магазине Sevenlight.ru вы найдете огромный ассортимент светильников от крупнейших мировых производителей. Удобная форма работы позволит вам не выходя из дома подобрать подходящую продукцию и сделать заказ онлайн. Профессиональные консультанты и менеджеры помогут вам на всех этапах совершения покупки, а низкие цены и оперативная доставка вас приятно удивят.</p><p>Четко структурированный каталог люстр позволит подобрать нужные изделия с учетом мельчайших нюансов. Используя расширенный поиск с применением разных фильтров можно найти <a href=\"/catalog/lyustry-po-tipu\">люстры по типу</a>, по стилю, по бренду, по цветовой гамме, основным характеристикам и другим критериям.</p><p>Если вы еще думаете, как выбрать люстру, чтобы покупка доставила только положительные эмоции, ждём вас в нашем интернет-магазине Sevenlight.ru. Регулярно на сайте проходит выгодная <a href=\"/catalog/rasprodazha/tip-lyustra\">распродажа люстр</a>, где вы сможете найти разнообразные модели светильников для оснащения любого интерьера по привлекательным ценам.</p>', 1);

CREATE TABLE `s_brands` (
  `id` smallint(5) UNSIGNED NOT NULL,
  `name` varchar(255) NOT NULL,
  `trans` varchar(255) CHARACTER SET ascii COLLATE ascii_general_ci NOT NULL,
  `trans2` varchar(255) NOT NULL DEFAULT '',
  `meta_title` varchar(500) DEFAULT '',
  `meta_keywords` varchar(500) DEFAULT '',
  `meta_description` varchar(500) DEFAULT '',
  `annotation` text NOT NULL,
  `description` mediumtext NOT NULL,
  `image` varchar(255) DEFAULT '',
  `image_id` int(10) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_cache_integer` (
  `updated` date DEFAULT '1000-01-01',
  `keyhash` binary(16) NOT NULL,
  `value` int(10) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_categories` (
  `id` smallint(5) UNSIGNED NOT NULL,
  `parent_id` smallint(5) UNSIGNED NOT NULL DEFAULT '0',
  `vparent_id` smallint(5) UNSIGNED NOT NULL DEFAULT '0',
  `vcat1` varchar(400) CHARACTER SET ascii COLLATE ascii_bin NOT NULL DEFAULT '',
  `vcat2` varchar(400) CHARACTER SET ascii COLLATE ascii_bin NOT NULL DEFAULT '',
  `name` varchar(255) NOT NULL,
  `name_singular` varchar(255) NOT NULL DEFAULT '',
  `meta_title` varchar(255) DEFAULT '',
  `meta_keywords` varchar(3076) DEFAULT '',
  `meta_description` varchar(500) DEFAULT '',
  `auto_meta_keywords` text NOT NULL,
  `auto_meta_description` text NOT NULL,
  `auto_meta_title` text NOT NULL,
  `auto_annotation` text NOT NULL,
  `auto_description` text NOT NULL,
  `annotation` text NOT NULL,
  `description` mediumtext NOT NULL,
  `trans` varchar(255) DEFAULT '',
  `trans2` varchar(255) NOT NULL DEFAULT '',
  `image` varchar(255) DEFAULT '',
  `image_id` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `pos` mediumint(8) UNSIGNED NOT NULL DEFAULT '0',
  `visible` tinyint(1) UNSIGNED NOT NULL DEFAULT '1',
  `enabled` tinyint(1) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_categories_features` (
  `category_id` smallint(5) UNSIGNED NOT NULL,
  `feature_id` smallint(5) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_comments` (
  `id` int(10) UNSIGNED NOT NULL,
  `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `ip` varchar(20) DEFAULT '',
  `object_id` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `name` varchar(255) DEFAULT '',
  `text` text,
  `type` enum('product','blog') DEFAULT 'blog',
  `approved` int(1) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_coupons` (
  `id` smallint(5) UNSIGNED NOT NULL,
  `code` varchar(256) NOT NULL,
  `expire` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `type` enum('absolute','percentage') NOT NULL DEFAULT 'absolute',
  `value` decimal(10,2) UNSIGNED NOT NULL DEFAULT '0.00',
  `min_order_price` decimal(10,2) UNSIGNED DEFAULT '0.00',
  `single` tinyint(1) UNSIGNED NOT NULL DEFAULT '0',
  `usages` mediumint(8) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_currencies` (
  `id` mediumint(8) UNSIGNED NOT NULL,
  `name` varchar(255) NOT NULL DEFAULT '0',
  `sign` varchar(20) DEFAULT '',
  `code` char(3) DEFAULT '',
  `rate` float UNSIGNED NOT NULL DEFAULT '1',
  `cents` tinyint(1) UNSIGNED NOT NULL DEFAULT '2',
  `pos` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `enabled` int(1) NOT NULL DEFAULT '1'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

