INSERT INTO `s_pages` (`id`, `trans`, `name`, `meta_title`, `meta_description`, `meta_keywords`, `body`, `menu_id`, `pos`, `visible`, `header`, `new_field`, `new_field2`) VALUES
(1, '', '', 'Интернет магазин люстр и светильников в Москве и по всей России Sevenlight.ru', 'Интернет-магазин люстр и светильников Sevenlight.ru - Доставка по Москве и всей России, самовывоз: г. Москва, окружной проезд 19, тел. 8-(495)960-66-59\r\n', '', '<p>Добро пожаловать в интернет-магазин Sevenlight.ru, мы предлагаем современные модели люстр, светильников, настольных ламп, торшеров и бра мировых производителей. Все товары сертифицированы. Мы осуществляем оперативную доставку, оказываем консультации по любым вопросам, связанным с освещением, подбираем светильники и люстры под Ваши запросы. Предоставляем скидки на Ваши заказы! С Вами всегда будет работать персональный менеджер!', 1, 1, 1, 'Главная', '', ''),
(2, 'oplata', 'Оплата', 'Оплата', 'Оплата', 'Оплата', '<h2><span>Наличными курьеру</span></h2><p>Вы можете оплатить заказ курьеру в гривнах непосредственно в момент доставки. Курьерская доставка осуществляется по Москве на следующий день после принятия заказа.</p><h2>Webmoney</h2><p>После оформления заказа вы сможете перейти на сайт webmoney для оплаты заказа, где сможете оплатить заказ в автоматическом режиме, а так же проверить наш сертификат продавца.</p><h2>Наличными в офисе Автолюкса</h2><p>При доставке заказа системой Автолюкс, вы сможете оплатить заказ в их офисе непосредственно в момент получения товаров.</p>', 1, 8, 0, 'Способы оплаты', '', ''),
(3, 'dostavka', 'Доставка', 'Доставка', 'Доставка', 'Доставка', '<h2>Курьерская доставка по&nbsp;Москве</h2><p>Курьерская доставка осуществляется на следующий день после оформления заказа<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>если товар есть в&nbsp;наличии. Курьерская доставка осуществляется в&nbsp;пределах Томска и&nbsp;Северска ежедневно с&nbsp;10.00 до&nbsp;21.00. Заказ на&nbsp;сумму свыше 300 рублей доставляется бесплатно. <br /><br />Стоимость бесплатной доставки раcсчитывается от&nbsp;суммы заказа с&nbsp;учтенной скидкой. В&nbsp;случае если сумма заказа после применения скидки менее 300р<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>осуществляется платная доставка. <br /><br />При сумме заказа менее 300 рублей стоимость доставки составляет от 50 рублей.</p><h2>Самовывоз</h2><p>Удобный<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>бесплатный и быстрый способ получения заказа.<br />Адрес офиса: Москва<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>ул. Арбат<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>1/3<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>офис 419.</p><h2>Доставка с&nbsp;помощью предприятия<span style=\"margin-right: 0.44em;\"> </span><span style=\"margin-left: -0.44em;\">&laquo;</span>Автотрейдинг&raquo;</h2><p>Удобный и быстрый способ доставки в крупные города России. Посылка доставляется в офис<span style=\"margin-right: 0.44em;\"> </span><span style=\"margin-left: -0.44em;\">&laquo;</span>Автотрейдинг&raquo; в&nbsp;Вашем городе. Для получения необходимо предъявить паспорт и&nbsp;номер грузовой декларации<span style=\"margin-right: 0.3em;\"> </span><span style=\"margin-left: -0.3em;\">(</span>сообщит наш менеджер после отправки). Посылку желательно получить в&nbsp;течение 24 часов с&nbsp;момента прихода груза<span style=\"margin-right: -0.2em;\">,</span><span style=\"margin-left: 0.2em;\"> </span>иначе компания<span style=\"margin-right: 0.44em;\"> </span><span style=\"margin-left: -0.44em;\">&laquo;</span>Автотрейдинг&raquo; может взыскать с Вас дополнительную оплату за хранение. Срок доставки и стоимость Вы можете рассчитать на сайте компании.</p><h2>Наложенным платежом</h2><p>При доставке заказа наложенным платежом с помощью<span style=\"margin-right: 0.44em;\"> </span><span style=\"margin-left: -0.44em;\">&laquo;</span>Почты России&raquo;, вы&nbsp;сможете оплатить заказ непосредственно в&nbsp;момент получения товаров.</p>', 1, 9, 0, 'Способы доставки', '', ''),
(4, 'blog', 'Блог', 'Блог', '', 'Блог', '', 1, 11, 1, 'Блог', '', ''),
(5, '404', '', 'Страница не найдена', 'Страница не найдена', 'Страница не найдена', 'Увы, такой страницы не существует :-( \r\nВ качестве комплемента мы дарим Вам скидку 5% на любой заказ, превышающий по цене 5000 рублей.\r\n', 2, 5, 1, 'Страница не найдена', '', ''),
(6, 'contact', 'Контакты', 'Контактная информация интернет магазина Sevenlight.ru', 'Контакты, адрес, телефон, график работы интернет магазина Sevenlight.ru', 'Контакты', '<div class=\"bx_page\">\r\n<ul class=\"contactList\">\r\n<li>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><img title=\"cont1.png\" src=\"/design/{$settings->theme}/images/cont1.png\" alt=\"cont1.png\" /></td>\r\n<td>+7 (495) 960-66-59<br />+7 (903) 960-66-59</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</li>\r\n<li>\r\n<table >\r\n<tbody>\r\n<tr >\r\n<td ><img title=\"cont2.png\" src=\"/design/{$settings->theme}/images/cont2.png\" alt=\"cont2.png\" /></td>\r\n<td ><a href=\"mailto:info@sevenlight.ru\">info@sevenlight.ru</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</li>\r\n<li>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><img title=\"cont3.png\" src=\"/design/{$settings->theme}/images/cont3.png\" alt=\"cont3.png\" /></td>\r\n<td>Пункт самовывоза/Офис<br />г. Москва, Окружной проезд, д.19&nbsp; &nbsp; &nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</li>\r\n<li>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><img title=\"cont4.png\" src=\"/design/{$settings->theme}/images/cont4.png\" alt=\"cont4.png\" /></td>\r\n<td>Пн-Пт : с 9:00 до 20:00<br />Сб, Вс : с 9:00 до 15:00</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</li>\r\n</ul>\r\n</div>', 1, 3, 1, 'Контакты', '', ''),
(7, 'products', 'Все товары', 'Все товары', '', 'Все товары', '', 2, 7, 0, 'Все товары', '', ''),
(8, 'o-nas', 'О нас', 'Об интернет магазине Sevenlight.ru', 'Об интернет магазине Sevenlight.ru, наши принципы работы и в чем наше преимущество', '', '<h1><strong>Об интернет магазине Sevenlight.ru</strong></h1><p>В сети очень много интернет магазинов светильников, которые так же как и мы занимаются продажей светового оборудования. <br />Наша фирма &ndash; это несколько энтузиастов, близких по духу единомышленников, которые отважились организовать торговлю бытового и профессионального освещения через собственный интернет канал в начале 2014 года.</p><p>Мы хотим внести ясность и подчеркнуть достоинства, показать отличия нашей небольшой фирмы от других подобных компаний.</p><ul><li>Т.к. Мы не являемся крупной организацией, то наши менеджеры не работают &ldquo;на потоке&rdquo; с клиентами, каждый клиент имеет персонального менеджера от первого звонка в наш интернет магазин и до того момента, когда клиент получил свой заказ.</li><li>Так же, спустя время (как правило в течение 2 недель) этот же менеджер ответит на все технические вопросы, связанные с приобретённым товаром. В том случае, если светильник не подошел, то наши менеджеры осуществят замену на другую модель, либо организуют акт приемки товара и возврат денежных средств. Мы уделяем этому особое внимание, т.к. знаем специфику торговли через интернет канал.</li><li>Все продаваемые нами товары &ndash; это крупные производители, имеющие все сопутствующие сертификаты и патенты, представляющие следующие страны: Италия, Германия, Австрия, Чехия, Польша, Бельгия, Америка, Россия, Китай.</li><li>По запросу клиентов мы всегда готовы предоставить оригинальные сертификаты производителя в электронном виде или в виде ксерокопии.</li><li>Большая часть этих производителей имеет свои производственные мощности в Китае. В Европе рабочая сила очень дорогая и многие европейские торговые марки производят свои товары в Китае, но по европейским стандартам качества, за этим строго следят, продукцию сертифицируют. Все товары подвергаются нескольким этапам проверок, за счет чего мы имеем очень низкий уровень рекламаций.</li></ul><h1><strong>Теперь еще немного о нашей работе</strong></h1><ul><li>Наши менеджеры всегда стараются узнать всю необходимую информацию о приобретаемом товаре у наших производителей на стадии предзаказа клиентов, если у них есть такая потребность.</li><li>Менеджер общается на прямую с производителем приобретаемого клиентом товара.</li><li>Благодаря накопленному опыту за немалый период времени, регулярному посещению выставок Interlight в Москве, постоянным знакомствам с новыми поставщиками, представляющими последние модели световых изделий, мы стали хорошо ориентироваться в сфере бытового освещения и всегда можем предложить нашим клиентам исчерпывающую информацию по аналогам и по уникальным моделям осветительных приборов как на Российском рынке, так и по Европе.</li><li>Наши менеджеры очень кропотливо подходят к вопросу подбора моделей люстр и светильников для клиентов. Мы стараемся задействовать все имеющиеся связи и порой привозим под заказ аналоги разыскиваемых светильников из Европы с кротчайшими сроками поставки от 10 дней до 2 недель. Реже сроки поставки составляют 3-4 недели.</li></ul><h1><strong>Работа с регионами</strong></h1><ul><li>Помимо Москвы и Московской области, мы работаем с регионами, отправляем люстры и светильники по всей России! Также у нас был удачный опыт работы с Европейскими клиентами! Мы отправляли светильни за рубеж и очень удачно.</li><li>За период работы в 4 года у нас ни разу не было случая, связанного с тем, чтобы мы отправили некондиционный товар, товар с наличием брака или не тот товар.</li><li>Мы с гордостью можем обозначить причину вышесказанного: Всегда, отправляя заказ в какой-либо регион России, мы собственноручно проверяем каждую коробку с товаром на складе на предмет целостности плафонов и отсутствия видимых дефектов на товарном изделии. Мы своими силами доставляем товар к транспортной компании, заказываем жесткую упаковку для отправляемого товара. И в итоге заказ к клиенту приходит проверенный и в сохранности.</li></ul><h1><strong>Итоги</strong></h1><p>Мы надеемся и верим, что обращаясь к нам, Вы не просто купите люстру или светильник, а получаете полноценный сопроводительный сервис и останетесь довольны обслуживанием нашими Менеджерами. Вам будет приятно работать с нами, ведь мы практикуем индивидуальный подход и персональное отношение к каждому нашему клиенту от пенсионерки, желающей приобрести себе уютный торшер в гостиную, до юридической организации, у которой стоит задание оснастить светом целый офисный корпус</p><p><em><strong>Всегда Ваш, с любовью, Sevenlight.ru</strong></em></p>', 1, 6, 1, 'О нас', '', ''),
(9, 'garantiya-i-kachestvo', 'Гарантия и качество', 'Гарантия и качество в интернет-магазине люстр и светильников Sevenlight.ru', 'Условия гарантии, возврат товара, интернет магазин Sevenlight.ru', 'Гарантия и качество', '<p><strong><img src=\"/design/luxury/images/garantiya.png\" alt=\"\" width=\"64\" height=\"64\" />&nbsp;ГАРАНТИЯ И КАЧЕСТВО</strong></p><p style=\"text-align: justify;\">&nbsp; Все товары, купленные в нашем интернет-магазине сертифицированны. <br /> Гарантия на продукцию распространяется на все товары, которые вы приобрели в нашем интернет-магазине. <br /> Если в течение недели вы обнаружили какие-либо дефекты у приобретенного товара, то мы произведем бесплатную замену этого товара.</p><ul><li>Мы торгуем только официальными брендами, имеющими систему контроля-качества, которые работают в рамках потребительского закона.</li><li>Товары всех производителей, предлагаемых нами подлежат возврату и обмену в случаи их брака.</li><li>Вы всегда можете запросить сертификаты интересующего Вас бренда и мы любезно предоставим Вам надлежащие документы.</li></ul><p>&nbsp;<br /><strong>ИНФОРМАЦИЯ К ОБЯЗАТЕЛЬНОМУ РАССМОТРЕНИЮ.</strong></p><p><strong>Порядок возврата товара для Регионов.</strong></p><ul><li>Возврат товара осуществляется в течение двух недель ( 14 календарных дней с момента покупки)</li><li>В том случае если, Товар возвращается по причине наличия брака, то услуги транспортной компании оплачинает продавец.</li><li>В случае же если, товар возвращается кондиционный и причины возврата не связаны с наличием брака<br /> (по каким-то причинам товар не подошел) и пр., то услуги ТК оплачивает сам покупатель</li><li>В любом случае &nbsp;(П.2 и П.3) клиент сам отвозит Товар в пункт приема ТК компаний в своем городе.</li><li>Если клиент решает отправить Товар ТК, с которой мы не работаем (почта России), то все риски по<br /> сохранности Товара клиент берет на себя.</li></ul><p><strong>Схема осуществления возврата: </strong></p><ul><li>Клиент заполняет заявление (форма) на возврат денежных средств, указывает реквизиты, по которым осуществляется возврат по безналичному расчету.</li><li>Далее клиент отправляет это заявление по электронной почте нам.</li><li>В течение 2-х рабочих дней принимается решение по возврату</li><li>Клиент доставляет Товар в ТК</li><li>Мы принимаем Товар</li><li>В течение 3-7 рабочих дней после приемки Товара мы возвращаем денежные средства клинту.</li></ul><p><strong>Порядок возврата товара для Москвы и ближайшего подмосковья.</strong></p><ul><li>Покупатель вправе передумать и отказаться от Товара до его передачи и после передачи в течении 14-ти рабочих дней.</li><li>Возврат товара предусмотрен в том случае, если сохранены его товарный вид, функциональность и документ, подтверждающий факт покупки надлежащего товара.</li><li>При отказе клиента от товара, Продавец обязан возвратить Покупателю денежную сумму, уплаченную последнему, за исключением расходов продавца на доставку от Покупателя возвращенного товара.</li><li>Денежная сумма, соответствующая стоимости товара ( не включая доставку) возвращается покупателю в течение 5-ти рабочих дней.</li><li>Возврат и обмен товара возможен только при сохранении его внешнего вида, сохранении функциональности, целостности упаковки и документов.</li><li>При обнаружении Вами каких-либо дефектов у Товара в течение 7 дней, мы произведем бесплатную замену этого товара.</li></ul>', 1, 4, 1, 'Гарантия и качество', '', ''),
(10, 'dostavka-i-oplata', 'Доставка и оплата', 'Условия доставки и оплаты в интернет магазине Sevenlight.ru', 'Условия доставки и оплаты в в интернет магазине Sevenlight.ru', 'Доставка и оплата', '<h2><img src=\"/design/luxury/images/punkt_samovivoza.png\" alt=\"\" width=\"75\" height=\"75\" /><strong>ПУНК САМОВЫВОЗА</strong></h2><p></p><ul><li>г. Москва, ул. Ткацкая д. 5 c 10-00 до&nbsp;18-00</li></ul><p></p><h2><img src=\"/design/luxury/images/dostavka_red_2.png\" alt=\"\" width=\"75\" height=\"75\" /> <strong>ДОСТАВКА ПО МОСКВЕ И ПОДМОСКОВЬЮ</strong></h2><p></p><p><strong>ПРИ ЗАКАЗЕ НА СУММУ ДО 5000 р. - 500 р.</strong></p><p><strong>БОЛЕЕ 5000 р. - БЕСПЛАТНО</strong></p><p><strong>МОСКОВСКАЯ ОБЛАСТЬ ЗА МКАД: 35 р. &ndash; 1 км.</strong></p><p></p><ul><li>Доставка заказов: от 1 до 3 дней.</li><li>Доставка в выходные и праздничные дни осуществляется по предворительной договоренности.</li><li>Звонок курьера за час до доставки</li><li>Бережная транспортировка</li><li>Доставка до квартиры или офиса независимо от этажа (даже, если в доме нет лифта).</li></ul><p></p><h2><img src=\"/design/luxury/images/dostavka_red_russia_2.png\" alt=\"\" width=\"107\" height=\"75\" /> <strong>ДОСТАВКА ПО РОССИИ</strong></h2><p></p><p><strong>ПРИ ЗАКАЗЕ НА СУММУ ДО 35 000 р. - СТОИМОСТЬ ДОСТАВКИ РАСЧИТЫВАЕТСЯ ПО ТАРИФАМ Т.К.</strong></p><p><strong>ПРИ ЗАКАЗЕ НА СУММУ ОТ 35 000 р. - СОГЛАСОВЫВАЕТСЯ С МЕНЕДЖЕРОМ</strong></p><p><strong>(средняя стоимость доставки в центральные города России составляет 500 р.)</strong></p><ul><li>Доставка заказов до ТК: 1 - 3 дня.</li><li>Производится транспортными и курьерскими компаниями</li><li>(ПЭК, СДЭК, Деловые Линии) или любым удобным для Вас грузоперевозчиком. Все необходимые сопроводительные документы вкладываются в отправление.</li></ul><p></p><h2><br /><img src=\"/design/luxury/images/oplata_1.png\" alt=\"\" width=\"56\" height=\"56\" /><strong>СПОСОБЫ ОПЛАТЫ</strong></h2><p><strong><br />Наличный расчёт</strong></p><ul><li>Вы можете оплатить заказ наличными курьеру при его получении или в пункте самовывоза.</li><li>При получении товара обязательно проверьте комплектацию заказа, наличие гарантийного талона и чека.</li></ul><p><strong>Безналичный расчёт</strong></p><ul><li>После оформления заказа будет сформирован счёт на оплату, который Вы можете распечатать и оплатить. Денежные средства поступят на наш счёт в течение 2-3 рабочих дней после оплаты заказа.</li><li>Оплата заказов клиентами - юридическими лицами возможна только по безналичному расчёту. <br />Все необходимые для бухгалтерии документы (оригинал счёта на оплату, счёт-фактура, накладная) выдаются вместе с заказом при получении.</li></ul>', 1, 2, 1, 'Доставка и оплата', '', ''),
(11, 'ustanovka', 'Установка', 'Установка люстр и светильников в интернет магазине Sevenlight.ru', 'Расценки и условия на установку люстр и светильников в интернет магазине Sevenlight.ru', 'Установка', '<!--Установка--><p>Мы осуществляем сборку и установку люстр и любых осветительных приборов, приобретенных в нашем интернет магазине по Москве и МО.</p><p>Наши монтажники - это сертифицированные профессионалы, занимающиеся монтажными работами бытовых и специализированных осветительных приборов не один год. <br />Заказывая услугу по сборке и установке люстр и светильников у нас, вы можете быть уверены, что работа будет выполнена в наилучшем виде.</p><p><img style=\"max-width: 100%;\" src=\"/design/luxury/images/mont2.jpg\" alt=\"Монтаж люстр\" title=\"Монтаж люстр\" /></p><h2>Прайс лист</h2><ul><li><strong>При стоимости люстры от 10 000 руб. скидка на установку 50%.</strong></li><li><strong>Установка больших люстр стоимостью свыше 20 000 руб., габаритных, дизайнерских, - обсуждается индивидуально, в частных случаях, возможна БЕСПЛАТНАЯ установка, все подробности у нашего менеджера.</strong></li></ul><ul><li><strong>1.</strong> Установка люстры на анкерах -1100р.</li><li><strong>2.</strong> Установка люстры на крюке -900р.</li><li><strong>3.</strong> Установка крюка -250р.</li><li><strong>4.</strong>Сборка люстры -450р. (Сборка люстры,коммутация проводов,навес украшений за 1 ярус).</li><li><strong>5.</strong> Сборка хрустальной люстры + 400р. к базовой стоимости.</li><li><strong>6.</strong> Переделка люстры под двухклавишный выключатель -200р.</li><li><strong>7.</strong> Демонтаж люстры -250р.</li><li><strong>8.</strong> Сборка напольного торшера -1100р.</li><li><strong>9.</strong> Установка настенных светильников,бра -650 р/шт.</li><li><strong>10.</strong> Выезд за МКАД -30р/км. Минимальный выезд -1100р.</li></ul><h2>Сроки монтажа</h2><ul><li>День в день.</li><li>На следующий день после доставки.</li><li>Вы выбираете день и мы согласовываем монтаж в этот день.</li></ul>', 1, 10, 1, 'Установка', '', '');

CREATE TABLE `s_payment_methods` (
  `id` smallint(5) UNSIGNED NOT NULL,
  `module` varchar(255) DEFAULT '',
  `name` varchar(255) DEFAULT '',
  `description` text,
  `currency_id` float DEFAULT '0',
  `settings` text,
  `enabled` tinyint(1) UNSIGNED NOT NULL DEFAULT '0',
  `pos` int(10) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

INSERT INTO `s_payment_methods` (`id`, `module`, `name`, `description`, `currency_id`, `settings`, `enabled`, `pos`) VALUES
(1, 'Receipt', 'Квитанция', '<p>Вы можете распечатать квитанцию и оплатить её в любом отделении банка.</p>', 2, 'a:10:{s:9:\"recipient\";s:65:\"ООО \"Великолепный интернет-магазин\"\";s:3:\"inn\";s:5:\"12345\";s:7:\"account\";s:6:\"223456\";s:4:\"bank\";s:18:\"Альфабанк\";s:3:\"bik\";s:6:\"556677\";s:21:\"correspondent_account\";s:11:\"77777755555\";s:8:\"banknote\";s:7:\"руб.\";s:5:\"pense\";s:7:\"коп.\";s:5:\"purse\";s:2:\"ru\";s:10:\"secret_key\";s:0:\"\";}', 1, 2),
(2, 'Webmoney', 'Webmoney wmz', '<p><span></span></p><div><p>Оплата через платежную систему&nbsp;<a href=\"http://www.webmoney.ru\">WebMoney</a>. У вас должен быть счет в этой системе для того, чтобы произвести оплату. Сразу после оформления заказа вы будете перенаправлены на специальную страницу системы WebMoney, где сможете произвести платеж в титульных знаках WMZ.</p></div><p>&nbsp;</p>', 3, 'a:10:{s:9:\"recipient\";s:0:\"\";s:3:\"inn\";s:0:\"\";s:7:\"account\";s:0:\"\";s:4:\"bank\";s:0:\"\";s:3:\"bik\";s:0:\"\";s:21:\"correspondent_account\";s:0:\"\";s:8:\"banknote\";s:7:\"руб.\";s:5:\"pense\";s:0:\"\";s:5:\"purse\";s:13:\"Z111111111111\";s:10:\"secret_key\";s:13:\"testsecretkey\";}', 1, 1),
(3, 'Robokassa', 'Робокасса', '<p><span>RBK Money &ndash; это электронная платежная система, с помощью которой Вы сможете совершать платежи с персонального компьютера, коммуникатора или мобильного телефона.</span></p>', 3, 'a:14:{s:9:\"recipient\";s:0:\"\";s:3:\"inn\";s:0:\"\";s:7:\"account\";s:0:\"\";s:4:\"bank\";s:0:\"\";s:3:\"bik\";s:0:\"\";s:21:\"correspondent_account\";s:0:\"\";s:8:\"banknote\";s:0:\"\";s:5:\"pense\";s:0:\"\";s:5:\"login\";s:0:\"\";s:9:\"password1\";s:0:\"\";s:9:\"password2\";s:0:\"\";s:8:\"language\";s:2:\"ru\";s:5:\"purse\";s:0:\"\";s:10:\"secret_key\";s:0:\"\";}', 1, 3),
(4, 'Paypal', 'PayPal', '<p>Совершайте покупки безопасно, без раскрытия информации о своей кредитной карте. PayPal защитит вас, если возникнут проблемы с покупкой</p>', 1, 'a:16:{s:8:\"business\";s:0:\"\";s:4:\"mode\";s:7:\"sandbox\";s:9:\"recipient\";s:0:\"\";s:3:\"inn\";s:0:\"\";s:7:\"account\";s:0:\"\";s:4:\"bank\";s:0:\"\";s:3:\"bik\";s:0:\"\";s:21:\"correspondent_account\";s:0:\"\";s:8:\"banknote\";s:0:\"\";s:5:\"pense\";s:0:\"\";s:5:\"login\";s:0:\"\";s:9:\"password1\";s:0:\"\";s:9:\"password2\";s:0:\"\";s:8:\"language\";s:2:\"ru\";s:5:\"purse\";s:0:\"\";s:10:\"secret_key\";s:0:\"\";}', 1, 4),
(5, 'Interkassa', 'Оплата через Интеркассу', '<p><span>Это удобный в использовании сервис, подключение к которому позволит Интернет-магазинам, веб-сайтам и прочим торговым площадкам принимать все возможные формы оплаты в максимально короткие сроки.</span></p>', 2, 'a:2:{s:18:\"interkassa_shop_id\";s:3:\"123\";s:21:\"interkassa_secret_key\";s:3:\"123\";}', 1, 5),
(6, 'Liqpay', 'Оплата картой через Liqpay.com', '<p><span>Благодаря своей открытости и универсальности LiqPAY стремительно интегрируется со многими платежными системами и платформами и становится стандартом платежных операций.</span></p>', 2, 'a:5:{s:9:\"liqpay_id\";s:3:\"123\";s:11:\"liqpay_sign\";s:3:\"123\";s:12:\"pay_way_card\";s:1:\"1\";s:14:\"pay_way_liqpay\";s:1:\"1\";s:15:\"pay_way_delayed\";s:1:\"1\";}', 1, 6),
(7, 'Pay2Pay', 'Оплата через Pay2Pay', '<p>Универсальный платежный сервис Pay2Pay призван облегчить и максимально упростить процесс приема электронных платежей на Вашем сайте. Мы открыты для всего нового и сверхсовременного.</p>', 2, 'a:5:{s:18:\"pay2pay_merchantid\";s:3:\"123\";s:14:\"pay2pay_secret\";s:3:\"123\";s:14:\"pay2pay_hidden\";s:3:\"123\";s:15:\"pay2pay_paymode\";s:3:\"123\";s:16:\"pay2pay_testmode\";s:1:\"1\";}', 1, 7),
(8, 'Qiwi', 'Оплатить через QIWI', '<p><span>QIWI &mdash; удобный сервис для оплаты повседневных услуг</span></p>', 2, 'a:2:{s:10:\"qiwi_login\";s:3:\"123\";s:13:\"qiwi_password\";s:3:\"123\";}', 1, 8);

CREATE TABLE `s_products` (
  `id` int(10) UNSIGNED NOT NULL,
  `votes` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `rating` float UNSIGNED NOT NULL DEFAULT '0',
  `views` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `trans` varchar(255) NOT NULL,
  `trans2` varchar(255) NOT NULL DEFAULT '',
  `image` varchar(255) NOT NULL DEFAULT '',
  `image_id` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `brand_id` smallint(5) UNSIGNED NOT NULL DEFAULT '0',
  `cat_id` mediumint(9) NOT NULL DEFAULT '0',
  `name` varchar(500) NOT NULL,
  `name_short` varchar(500) NOT NULL DEFAULT '',
  `annotation` text NOT NULL,
  `description` mediumtext NOT NULL,
  `visible` tinyint(1) UNSIGNED NOT NULL DEFAULT '1',
  `pos` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `meta_title` varchar(500) NOT NULL DEFAULT '',
  `meta_keywords` varchar(500) NOT NULL DEFAULT '',
  `meta_description` varchar(500) NOT NULL DEFAULT '',
  `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `featured` tinyint(1) UNSIGNED NOT NULL DEFAULT '0',
  `stock` tinyint(1) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_products_categories` (
  `product_id` int(10) UNSIGNED NOT NULL,
  `category_id` smallint(5) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_purchases` (
  `id` int(10) UNSIGNED NOT NULL,
  `order_id` int(10) UNSIGNED NOT NULL,
  `product_id` int(10) UNSIGNED DEFAULT '0',
  `variant_id` int(10) UNSIGNED DEFAULT '0',
  `product_name` varchar(255) DEFAULT '',
  `variant_name` varchar(255) DEFAULT '',
  `price` decimal(10,2) UNSIGNED NOT NULL DEFAULT '0.00',
  `amount` mediumint(8) UNSIGNED NOT NULL DEFAULT '0',
  `sku` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_queue` (
  `id` int(10) UNSIGNED NOT NULL,
  `keyhash` binary(16) NOT NULL,
  `method` varchar(15) CHARACTER SET ascii COLLATE ascii_general_ci NOT NULL,
  `task` varchar(15000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT ''
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

CREATE TABLE `s_related_products` (
  `product_id` int(10) UNSIGNED NOT NULL,
  `related_id` int(10) UNSIGNED NOT NULL,
  `pos` int(10) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `s_settings` (
  `setting_id` mediumint(8) UNSIGNED NOT NULL,
  `name` varchar(255) NOT NULL,
  `value` varchar(3000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

