ALTER TABLE `s_blog`
  ADD PRIMARY KEY (`id`),
  ADD KEY `enabled` (`visible`),
  ADD KEY `url` (`trans`);

ALTER TABLE `s_brands`
  ADD PRIMARY KEY (`id`),
  ADD UNIQUE KEY `name` (`name`) USING BTREE,
  ADD UNIQUE KEY `trans` (`trans`) USING BTREE,
  ADD KEY `id_name` (`id`,`name`),
  ADD KEY `trans2` (`trans2`) USING BTREE;

ALTER TABLE `s_cache_integer`
  ADD PRIMARY KEY (`keyhash`) USING BTREE,
  ADD UNIQUE KEY `keyhash_value` (`keyhash`,`value`),
  ADD KEY `updated` (`updated`);

ALTER TABLE `s_categories`
  ADD PRIMARY KEY (`id`),
  ADD KEY `url` (`trans`),
  ADD KEY `parent_id` (`parent_id`),
  ADD KEY `pos` (`pos`),
  ADD KEY `visible` (`visible`),
  ADD KEY `url2` (`trans2`),
  ADD KEY `enabled` (`enabled`);

ALTER TABLE `s_categories_features`
  ADD PRIMARY KEY (`feature_id`,`category_id`),
  ADD UNIQUE KEY `cid_fid` (`category_id`,`feature_id`) USING BTREE;

ALTER TABLE `s_comments`
  ADD PRIMARY KEY (`id`),
  ADD KEY `product_id` (`object_id`),
  ADD KEY `type` (`type`);

ALTER TABLE `s_coupons`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `s_currencies`
  ADD PRIMARY KEY (`id`),
  ADD UNIQUE KEY `name` (`name`) USING BTREE,
  ADD KEY `pos` (`pos`,`cents`,`code`,`enabled`,`id`,`name`,`rate`,`sign`) USING BTREE;

ALTER TABLE `s_delivery`
  ADD PRIMARY KEY (`id`),
  ADD KEY `pos` (`pos`);

ALTER TABLE `s_delivery_payment`
  ADD PRIMARY KEY (`delivery_id`,`payment_method_id`);

ALTER TABLE `s_features`
  ADD PRIMARY KEY (`id`),
  ADD UNIQUE KEY `name` (`name`),
  ADD UNIQUE KEY `trans` (`trans`),
  ADD KEY `pos` (`pos`),
  ADD KEY `in_filter` (`in_filter`),
  ADD KEY `trans2` (`trans2`);

ALTER TABLE `s_feedback`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `s_groups`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `s_img_blog`
  ADD PRIMARY KEY (`id`,`item_id`),
  ADD KEY `pos` (`pos`),
  ADD KEY `item_id` (`item_id`) USING BTREE,
  ADD KEY `basename` (`basename`) USING BTREE;

ALTER TABLE `s_img_categories`
  ADD PRIMARY KEY (`id`,`item_id`),
  ADD KEY `pos` (`pos`),
  ADD KEY `item_id` (`item_id`) USING BTREE,
  ADD KEY `basename` (`basename`) USING BTREE;

ALTER TABLE `s_img_products`
  ADD PRIMARY KEY (`id`) USING BTREE,
  ADD KEY `pos` (`pos`),
  ADD KEY `item_id` (`item_id`) USING BTREE,
  ADD KEY `basename` (`basename`) USING BTREE;

ALTER TABLE `s_labels`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `s_menu`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `s_options`
  ADD PRIMARY KEY (`product_id`),
  ADD KEY `only` (`product_id`);

ALTER TABLE `s_options_groups`
